	["cat_gear"] = "Varusteet",

    ["ak47"] = { name = "AK-47", desc = "Voimakas ja aina yhtä luotettava AK-47 on eräs maailman suosituimpia rynnäkkökivääreitä. Se on tappavimmillaan pieniä, hallittuja tulipurskeita ammuttaessa." },
    ["aug"] = { name = "AUG", desc = "AUG on tehokas ja tarkka kiikarirynnäkkökivääri, joka kompensoi pitkiä latausaikojaan tappavalla tarkkuudellaan ja suurella tulinopeudellaan." },
    ["awp"] = { name = "AWP", desc = "Pahamaineinen AWP on riskaabeli mutta hyödyllinen valinta. Sen tunnistaa äänestä ja siitä, että jokainen osuma on taattu tappo." },
    ["deserteagle"] = { name = "Desert Eagle", desc = "Desert Eagle on kallis ja tehokas, taitoa vaativa ja äärimmäisen suosittu pistooli, joka on pitkällä matkalla yllättävän tarkka." },
    ["deagle"] = "Desert Eagle",
    ["elites"] = { name = "Tupla-Berettat", desc = "Kahdella isolippaisella Berettalla yhtä aikaa ampuminen alentaa tarkkuutta ja hidastaa lataamista. Mutta toisaalta saa ampua kahdella isolippaisella Berettalla yhtä aikaa." },
    ["elite"] = "Tupla-Berettat",
    ["famas"] = { name = "FAMAS", desc = "FAMAS on penninvenyttäjän halpa vaihtoehto, joka osuu sopivasti kalliimpien kiväärien ja vähemmän tehokkaiden konepistoolien väliin." },
    ["fiveseven"] = { name = "Five-SeveN", desc = "Tarkka ja panssarin läpäisevä Five-SeveN on kallis, hitaasti lataava pistooli, joka tarjoaa vastapainona 20 panoksen lippaan ja pehmeän rekyylin." },
    ["flashbang"] = { name = "Valokranaatti", desc = "Valokranaatti ei tapa, mutta sokaisee hetkellisesti jokaisen iskukantaman alueella olevan, ja on siksi täydellinen vihollisen savustamiseen ulos ahtaista paikoista. Sen kova räjähdys peittää hetkellisesti myös askelten äänet." },
    ["g3sg1"] = { name = "G3SG1", desc = "Hintava G3SG1 hidastaa matkantekoasi huomattavasti, mutta ampuu paljon nopeammin kuin muut kiikarikiväärit." },
    ["galilar"] = { name = "Galil AR", desc = "Galil AR on terroristien rynnäkkökiväärien joukossa se halvempi vaihtoehto ja oiva ratkaisu keskipitkän ja pitkän matkan taisteluihin." },
    ["glock18"] = { name = "Glock-18", desc = "Glock 18 on hyödyllinen ensimmäisen kierroksen pistooli, joka pystyy ampumaan kolmen ammuksen sarjoja ja tehoaa parhaiten vihollisiin, joilla ei ole Kevlar-liiviä." },
    ["he_grenade"] = { name = "HE-kranaatti", desc = "Voimakas sirpalekranaatti kylvää tuhoa laajalle alueelle, ja on täydellinen suljettujen tilojen tyhjentämiseen vihollisista." },
    ["he_grenade_short"] = "HE-kranaatti",
    ["hegrenade"] = "HE-kranaatti",
    ["knife"] = { name = "Veitsi", desc = "Taisteluveitsi on hiljainen ja aina saatavilla. Sen tavallinen hyökkäys on nopea mutta heikko, vaihtoehtoinen hyökkäys tappavampi. Hiljaiset pelaajat saavat selkäänpuukottamalla välittömän tapon." },
    ["knife_t"] = "Veitsi",
    ["knife_gg"] = "Kultaveitsi",
    ["knife_ghost"] = { name = "Aaveveitsi", desc = "Kummitukset rakastavat kummitusveitsiä. Ole valppaana, kun ne saavat käsiinsä sellaisen! Ne yrittävät puukottaa ensimmäisen tilaisuuden tultua. Harmi kyllä, ne ovat kummituksia, eikä kummitusveitsetkään pysty vahingoittamaan ihmisiä...." },
    ["m249"] = { name = "M249", desc = "Tehokas ja avoimiin tiloihin soveltuva M249-konekivääri on täydellinen pelaajille, jotka haluavat lisää tarkkuutta ja suuremman ammusmäärän tulitusnopeuden kustannuksella." },
    ["m4a1"] = "M4A4",
    ["m4_silencer"] = "M4A1-S",
    ["m4a1_silencer"] = { name = "M4A1-S", desc = "Äänenvaimentimella varustettu M4A1 on pienilippaisempi ja hiljaisempi vaihtoehto, joka takaa tarkat laukaukset." },
    ["mac10"] = { name = "MAC-10", desc = "MAC-10 on käytännössä laatikko, joka syytää luoteja kuin tykinsuusta, mutta tarkkuudessa ja rekyylissä olisi toivomisen varaa." },
    ["p90"] = { name = "P90", desc = "P90 on helppo tunnistaa ainutlaatuisesta bullpup-mallistaan. Se sopii ison lippaan ja vähäisen rekyylin ansiosta liikkeessä ampumiseen." },
    ["smoke_grenade"] = { name = "Savukranaatti", desc = "Savukranaatti tuottaa keskikokoisen savuverhon. Sen avulla voit suojella tiimiäsi tehokkaasti tarkka-ampujilta, tai vaikka tehdä harhautuksia." },
    ["smokegrenade"] = "Savukranaatti",
    ["ump45"] = { name = "UMP-45", desc = "UMP45 on konepistoolien keskivertoa. Sen pieni lipas on aseen ainoa huono puoli, muuten se on hyvin monipuolinen automaattiase lähitaisteluihin." },
    ["xm1014"] = { name = "XM1014", desc = "XM1014 on tehokas täysautomaattihaulikko, jonka kallista hintalappua kompensoi sen tulinopeus." },
    ["c4"] = { name = "C4-räjähde", desc = "Tämä improvisoitu muoviräjähde on vakaa ja kestää useimmat fyysiset rasitukset, ja sytytettäessä sen räjähdysnopeus on yli 8 000 m/s." },
    ["bizon"] = { name = "PP-Bizon", desc = "Bizon-konepistooli ei tarjoa suurta tulivoimaa, mutta siinä on ainutlaatuinen, tilava rumpulipas, joka lataa nopeasti." },
    ["mag7"] = { name = "MAG-7", desc = "Erikoisjoukkojen Mag-7 takaa tappavan annoksen vahinkoa lähikantamalta. Nopea lipaslataus tekee siitä mahtavan taktisen vaihtoehdon." },
    ["negev"] = { name = "Negev", desc = "Negev pitää viholliset loitolla tarkalla tulituksellaan, kunhan sinulla on tarpeeksi aikaa saada se hallintaan." },
    ["sawedoff"] = { name = "Katkaistu haulikko", desc = "Klassinen katkaistu haulikko tekee hurjan määrän vahinkoa lähietäisyydeltä, mutta kun ase on epätarkka, ampuu hitaasti ja levittää haulit ympäriinsä, niin ensimmäisen osuman on paras tappaa." },
    ["tec9"] = { name = "Tec-9", desc = "Ihanteellinen pistooli liikkuvalle terroristille. Tec-9 tappaa lähitaistelussa ja tarjoaa suuren lippaan." },
    ["taser"] = { name = "Zeus x27", desc = "Zeus x27 on täydellinen lähikantaman väijytyksiin ja suljettujen tilojen taisteluihin, sillä se voi lamauttaa vihollisen yhdellä osumalla." },
    ["molotov"] = { name = "Polttopullo", desc = "Molotov on tehokas ja arvaamaton ase, joka leimahtaa liekkeihin osuessaan maahan ja vahingoittaa sen lähellä olevia pelaajia." },
