    ["hkp2000"] = { name = "P2000", desc = "Tarkka ja hallittava saksalaisvalmisteinen P2000 on hyödyllinen ensimmäisen erän pistooli, joka toimii parhaiten suojattomia vihollisia vastaan." },
    ["mp7"] = { name = "MP7", desc = "MP7 on monipuolinen ja kallis saksalainen konepistooli, joka on täydellinen lähitaistelutilanteissa." },
    ["mp5sd"] = { name = "MP5-SD", desc = "MP5 on ikoninen konepistooli, jota on yritetty turhaan jäljitellä. Se on ehkä maailman monikäyttöisin ja suosituin konepistooli. Tästä versiosta löytyy integroitu äänenvaimennin, joka tekee mahtavasta aseesta hiirenhiljaisen." },
    ["mp9"] = { name = "MP9", desc = "Sveitsiläinen MP9 on ergonominen polymeerikonepistooli, joka on varsinkin yksityisten vartiointifirmojen suosiossa." },
    ["nova"] = { name = "Nova", desc = "Nova on erittäin halpa väijytysase niille, joilla on matti kukkarossa." },
    ["p250"] = { name = "P250", desc = "P250 on hyvin vähän potkaiseva ja nopeasti ampuva käsiase, joka on halpa ratkaisu panssaroituja vihollisia vastaan." },
    ["cz75"] = "CZ75-Auto",
    ["scar20"] = { name = "SCAR-20", desc = "SCAR-20 on puoliautomaattinen kiikarikivääri, joka tulittaa nopeasti ja tehokkaasti pitkällä matkalla, mutta hidastaa liikettä ja maksaa hunajaa." },
    ["sg556"] = "SG 553",
    ["ssg08"] = { name = "SSG 08", desc = "SSG08-pulttilukkokivääri tekee vähäistä vahinkoa halpaan hintaan, joten se sopii hyvin alkuerien pitkän matkan tarkkuusammuntaan." },
