    ["cutters"] = "Pelastuspakkaus",
    ["revolver"] = { name = "R8-revolveri", desc = "R8-revolveri on varsin tarkka voimanpesä, mutta liipaisinveto hidas. Kun lähitaistelussa tarvitaan raakaa pysäytysvoimaa, nopea tulitus iskurvasaraa läpsimällä on paras vaihtoehto." },
    ["usp_silencer"] = { name = "USP-S", desc = "Vaimennettu USP-pistooli on pelaajien suosikki Counter-Strike: Sourcesta. Siihen kuuluu irrotettava äänenvaimennin, joka vähentää rekyyliä sekä huomiota herättävää ääntä." },
    ["knifebayonet"] = { name = "Pistin", desc = "Pistin ei ole juuri muuttunut sitten toisen maailmansodan, mutta sillä on silti paikkansa nykypäivän taistelutaktiikoissa. Pistinrynnäköt ovat pysyneet tehokkaina viime aikoinakin, kuten Persianlahden ja Afganistanin sodissa." },
    ["knifeflip"] = { name = "Taittoveitsi", desc = "Taittoveitsissä on persialaistyylinen kaareva terä, jossa on terävä kärki. Vaikka kärki itsessään saattaa olla särkyvä, taittoveitsi on yllättävän kestävä sen muotoilun vuoksi." },
    ["knifegut"] = { name = "Metsästysveitsi", desc = "Metsästysveitsen huomattavin ominaisuus on sen koukku terän selkäpuolella. Alun perin se yleistyi metsästyssaaliiden käsittelyn apuvälineenä, mutta leikkaa helposti myös narua, verkkoa tai turvavyön." },
    ["knifecss"] = { name = "Klassinen veitsi", desc = "Counter-Strike-sarjan klassikko. Puukon laita on puristettua stelliittiä, joka istuu täydellisesti muun terän titaaniin. Puukon kahva on fossilisoitunutta mastodontin luuta hiilikuitutupessa." },
    ["knifem9"] = { name = "M9-pistin", desc = "M9-pistin suunniteltiin alun perin kiinnitettäväksi kivääriin, mutta toimii hyvin myös lähikontaktissa." },
    ["knifekaram"] = { name = "Karambit", desc = "Karambit, jonka kaareva terä muistuttaa tiikerin kynttä, kehitettiin osana Kaakkois-Aasian silat-kamppailulajeja. Veistä pidetään yleensä terä alaspäin etusormen ollessa veitsen kahvassa olevassa sormiaukossa." },
    ["knifetactical"] = { name = "Metsämiehen veitsi", desc = "Moderneihin, taktisiin käyttötarkoituksiin suunniteltu veitsi, jonka terä sopii mainiosti käytettäväksi sekä taistelussa että työkaluna. Ainutlaatuinen Tanto-terä läpäisee kovimmatkin pinnat tehokkaasti." },
    ["knife_butterfly"] = { name = "Perhosveitsi", desc = "Tämä on mittatilauksena tehty balisong, joka tunnetaan yleisesti perhosveitsenä. Tämän aseen erityispiirteenä on sen vapaasti kääntyvän terän viuhkamainen avautuminen, joka sallii sen nopean käyttöönoton tai kätkemisen. Tämän seurauksena perhosveitset ovat laittomia monissa maissa." },
    ["partypopper"] = "Paukkuserpentiini!",
    ["knife_falchion_advanced"] = { name = "Falchion-veitsi", desc = "Nykyaikaisena kunnianosoituksena falchion-miekalle tässä terässä on kurvikas kärki." },
    ["knife_push"] = { name = "Varjotikarit", desc = "Tehokas mutta brutaali nyrkkipuukko yhtä yksinkertainen kuin nyrkinisku naamaan." },
    ["knife_survival_bowie"] = { name = "Bowie-puukko", desc = "Tämä täysruotorakenteinen, hammastettu Bowie-puukko on suunniteltu kovaan käyttöön brutaaleissa selviytymistilanteissa." },
