csstring = { -- Russian
	["cat_pistols"] = "Пистолеты",
	["cat_heavy"] = "Тяжелое оружие",
	["cat_smg"] = "Пистолеты-пулемёты",
	["cat_rifles"] = "Штурмовые винтовки",
	["cat_grenades"] = "Гранаты",
	["cat_gear"] = "Экипировка",

    ["ak47"] = { name = "AK-47", desc = "Мощная и надежная штурмовая винтовка АК-47 — одна из самых известных в мире. Наиболее смертельна стрельба короткими и точными очередями." },
    ["aug"] = { name = "AUG", desc = "Мощная и точная винтовка с оптическим прицелом AUG компенсирует свою долгую перезарядку низким разбросом пуль и высокой скорострельностью." },
    ["awp"] = { name = "AWP", desc = "Печально известная снайперская винтовка AWP — очень опасное и очень эффективное оружие, которое легко узнается по характерному звуку выстрела и жестокому правилу: «один выстрел, один труп»." },
    ["deserteagle"] = { name = "Desert Eagle", desc = "Дорогой и крайне мощный Desert Eagle — знаковый пистолет, который непросто освоить, хоть он и удивительно точен на дальних дистанциях." },
    ["deagle"] = "Desert Eagle",
    ["elites"] = { name = "Dual Berettas", desc = "Одновременная стрельба из двух пистолетов Beretta с большим магазином уменьшает точность и увеличивает время перезарядки. С другой стороны, вы можете стрелять сразу двумя Beretta." },
    ["elite"] = "Dual Berettas",
    ["famas"] = { name = "FAMAS", desc = "Дешёвый выбор для не очень богатых игроков, FAMAS отлично заполняет нишу между более дорогими автоматами и менее эффективными пистолетами-пулемётами." },
    ["fiveseven"] = { name = "Five-SeveN", desc = "Five-Seven стоит дорого и медленно перезаряжается, однако эти недостатки компенсируются высокой точностью, бронебойностью, щедрым магазином на 20 патронов и снисходительной отдачей." },
    ["flashbang"] = { name = "Светошумовая граната", desc = "Нелетальная светошумовая граната временно ослепляет всех, кто находится в радиусе взрыва, что делает её идеальным средством для зачистки закрытых помещений. Громкий взрыв гранаты также временно маскирует звуки шагов." },
    ["g3sg1"] = { name = "G3SG1", desc = "Дорогая снайперская винтовка G3SG1 значительно замедляет движение, что, однако, компенсируется более высокой скорострельностью, чем у других снайперских винтовок." },
    ["galilar"] = { name = "Автомат «Галиль»", desc = "Автомат «Галиль» — относительно дешевая штурмовая винтовка у террористов, которая считается хорошим оружием на средних и дальних дистанциях." },
    ["glock18"] = { name = "Glock-18", desc = "Пистолет Glock-18 подходит для первого раунда. Он наиболее эффективен против противников без бронежилета и может вести огонь очередями из трёх выстрелов." },
    ["he_grenade"] = { name = "Осколочная граната", desc = "Фугасная осколочная граната наносит большое количество урона в широкой области, что делает её идеальным средством для зачистки врагов в закрытых помещениях." },
    ["he_grenade_short"] = "Осколочная граната",
    ["hegrenade"] = "Осколочная граната",
    ["knife"] = { name = "Нож", desc = "Тихий и всегда доступный нож имеет быструю основную атаку с низким уровнем повреждений и вторичную атаку — медленную, но зато с высоким уровнем повреждений. Атака со спины позволит тихим игрокам моментально убить противника." },
    ["knife_t"] = "Нож",
    ["knife_gg"] = "Золотой нож",
    ["knife_ghost"] = { name = "Призрачная заточка", desc = "Призракам нравятся призрачные ножи. Если такой попадётся им в руки — берегитесь! Они тут же попытаются зарезать вас. Вот только призрачные ножи людей ранить не могут..." },
    ["m249"] = { name = "M249", desc = "Пулемет M249 хорош на открытых пространствах и идеально подходит игрокам, готовым мириться с медленным темпом стрельбы ради вместительного магазина и повышенной точности." },
    ["m4a1"] = "M4A4",
    ["m4_silencer"] = "M4A1-S",
    ["m4a1_silencer"] = { name = "M4A1-S", desc = "Бесшумный карабин M4A1 оснащен менее вместительным магазином, чем его аналог без глушителя, зато он обеспечивает более тихую стрельбу с меньшей отдачей и повышенной точностью." },
    ["mac10"] = { name = "MAC-10", desc = "Пистолет-пулемёт MAC-10 похож на коробку, из которой летят пули. Он может похвастаться высокой скорострельностью, но расплатиться за это придется низкой точностью и сильной отдачей." },
    ["p90"] = { name = "P90", desc = "P90 легко узнать по уникальному дизайну под названием «булл-пап». Это оружие великолепно подходит для стрельбы на ходу благодаря высокой ёмкости магазина и низкой отдаче." },
    ["smoke_grenade"] = { name = "Дымовая граната", desc = "Дымовая граната создает дымовую завесу средней площади. Благодаря ей вы можете эффективно скрыть вашу команду от вражеских снайперов, или же просто отвлечь противника." },
    ["smokegrenade"] = "Дымовая граната",
    ["ump45"] = { name = "UMP-45", desc = "Пистолет-пулемёт UMP45 часто не воспринимают всерьёз, хотя небольшой магазин является его единственным недостатком. В остальном же это весьма универсальное оружие для боя на ближней дистанции." },
    ["xm1014"] = { name = "XM1014", desc = "XM1014 — это мощный, полностью автоматический дробовик, который оправдывает свой здоровенный размер возможностью быстро нашпиговать комнату свинцом." },
    ["c4"] = { name = "Взрывчатка C4", desc = "Это самодельное взрывное устройство стабильно и устойчиво к большинству механических ударов, а также имеет скорость детонации более 8000 метров в секунду при воспламенении." },
    ["bizon"] = { name = "ПП-19 «Бизон»", desc = "У пистолета-пулемёта «Бизон» низкий урон, который компенсируется уникальным вместительным шнековым магазином с быстрой перезарядкой." },
    ["mag7"] = { name = "MAG-7", desc = "Эксклюзивный для спецназа Mag-7 наносит разрушительный ущерб на близком расстоянии. Быстрая перезарядка магазина делает его отличным тактическим выбором." },
    ["negev"] = { name = "Негев", desc = "Невероятно мощный пулемёт «Негев» позволяет держать противника на почтительном расстоянии точным огнём на подавление — если, разумеется, вам хватит времени заполучить желаемую точность." },
    ["sawedoff"] = { name = "Sawed-Off", desc = "Классический обрез наносит большой урон на ближних дистанциях, но имеет невысокую точность, высокий разброс и медленную скорость стрельбы, поэтому вам лучше бы поскорее убивать то, во что вы попали." },
    ["tec9"] = { name = "Tec-9", desc = "Tec-9 обладает вместительным магазином и идеально подходит для террориста, предпочитающего бой на небольших расстояниях." },
    ["taser"] = { name = "Zeus x27", desc = "Однозарядный Zeus x27 идеален для засад и стычек в закрытом пространстве, также он способен нейтрализовать врага всего с одного выстрела." },
    ["molotov"] = { name = "Коктейль Молотова", desc = "Коктейль Молотова — это мощное и непредсказуемое оружие, которое при броске разливается пламенем по небольшой области, нанося урон любому игроку в радиусе поражения." },
    ["incgrenade"] = "Зажигательная граната",
    ["incgrenade_short"] = "Зажигательная граната",
    ["decoy"] = "Ложная граната",
    ["decoy_short"] = "Ложная граната",
    ["hkp2000"] = { name = "P2000", desc = "Точный и податливый пистолет P2000, произведенный в Германии, — хорошее оружие для первого раунда, которое особенно эффективно против небронированных противников." },
    ["mp7"] = { name = "MP7", desc = "Универсальный, но дорогой пистолет-пулемёт немецкого производства MP7 — идеальный выбор для перестрелки на ближних дистанциях." },
    ["mp5sd"] = { name = "MP5-SD", desc = "Единственный и неповторимый MP5 — пожалуй, самый универсальный и популярный пистолет-пулемёт в мире. Модификация SD оснащена интегрированным глушителем, который делает бесшумным и без того грозное оружие." },
    ["mp9"] = { name = "MP9", desc = "Производимый в Швейцарии передовой пистолет-пулемёт МР9 — это эргономичное полимерное оружие, используемое частными охранными фирмами." },
    ["nova"] = { name = "Nova", desc = "Дробовик Nova из-за низкой цены идеально подходит для небогатых игроков, которые стремятся напасть на врага из засады." },
    ["p250"] = { name = "P250", desc = "Пистолет P250 — огнестрельное оружие с высоким темпом стрельбы и низкой отдачей, представляющее собой относительно недорогой выбор против бронированных противников." },
    ["cz75"] = "CZ75-Auto",
    ["scar20"] = { name = "SCAR-20", desc = "SCAR-20 — полуавтоматическая снайперская винтовка, которая предлагает высокую скорострельность и большой урон на расстоянии, в обмен на более медленную скорость передвижения и большую стоимость самого оружия." },
    ["sg556"] = "SG 553",
    ["ssg08"] = { name = "SSG 08", desc = "Снайперская винтовка SSG08 со скользящим затвором не наносит много повреждений, однако хорошее соотношение цены и качества делает ее отличным приобретением в первых раундах для перестрелок на дальних дистанциях." },
    ["heavyassaultsuit"] = "Комплект тяжёлой брони",
    ["assaultsuit"] = "Бронежилет и шлем",
    ["assaultsuit_only_helmet"] = "Шлем",
    ["kevlar"] = "Бронежилет",
    ["defuser"] = "Набор сапёра",
    ["cutters"] = "Спасательный набор",
    ["revolver"] = { name = "Револьвер R8", desc = "Револьвер R8 стреляет крайне точно и мощно, но его спусковой крючок печально известен своей тугостью. Если противник уже близко, не стесняйтесь бить по курку." },
    ["usp_silencer"] = { name = "USP-S", desc = "Пистолет USP, любимец игроков в Counter-Strike: Source, имеет отсоединяемый глушитель, который уменьшает отдачу, одновременно поглощая привлекающий внимание шум." },
    ["knifebayonet"] = { name = "Штык-нож", desc = "Со времен Второй мировой войны его дизайн принципиально не изменился, но штык по-прежнему остается на вооружении в современной армии. Его пользу оценили, например, в периоды второй войны в Персидском заливе и войны в Афганистане." },
    ["knifeflip"] = { name = "Складной нож", desc = "Спортивные складные ножи персидского стиля со стреловидным лезвием и очень острой заточкой. В то время как лезвие может быть хрупким, сам складной нож удивительно прочный." },
    ["knifegut"] = { name = "Нож с лезвием-крюком", desc = "Наиболее примечательной особенностью ножа-потрошителя является крюк для потрошения на тыльной стороне лезвия. Первоначально применявшийся как вспомогательное средство для свежевания животных, он также эффективен в прорезании волокнистых материалов, таких как веревки, стропы или ремни безопасности." },
    ["knifecss"] = { name = "Классический нож", desc = "Классика серии Counter-Strike. Лезвие из спрессованного стеллита идеально держат титановые бока и обух, а рукоятку из окаменелой мамонтовой кости окутывает углеволоконный чехол." },
    ["knifem9"] = { name = "Штык-нож M9", desc = "Это штык от М-9. Первоначально предназначался для установки на винтовке, но отлично подходит и для ближнего боя." },
    ["knifekaram"] = { name = "Керамбит", desc = "Керамбит — это нож с изогнутым лезвием, имитирующим клык тигра, специально разработанный для юго-восточного азиатского боевого искусства силат. Нож обычно держат обратной хваткой, вдевая указательный палец в кольцо на рукоятке." },
    ["knifetactical"] = { name = "Охотничий нож", desc = "Современный тактический нож, отлично подходящий как для боевых, так и мирных нужд. Уникальная заточка танто позволяет хорошо пробивать даже самые жесткие поверхности." },
    ["knife_butterfly"] = { name = "Нож-бабочка", desc = "Специально разработанный балисонг, широко известный как нож-бабочка. Главной особенностью этого оружия является молниеносное приведение его в боевое состояние при помощи взмаха руки, обнажающего свободно крепящееся лезвие. Как результат, ножи-бабочки запрещены во многих странах." },
    ["partypopper"] = "Хлопушка!",
    ["knife_falchion_advanced"] = { name = "Фальшион", desc = "Переосмысление клинка-фальшиона с изогнутым лезвием." },
    ["knife_push"] = { name = "Тычковые ножи", desc = "Наносить эффективные и жестокие удары тычковым ножом так же просто, как бить кулаками." },
    ["knife_survival_bowie"] = { name = "Нож Боуи", desc = "Этот цельный зазубренный нож Боуи создан для выживания в экстремальных условиях." },
    ["healthshot"] = "Медшприц",
    ["tagrenade"] = "Тактическая граната",
    ["exploding_barrel"] = "Взрывная бочка",
    ["snowball"] = "Снежок",
    ["stompdamagekillstring"] = "раздавил ваш череп",
    ["dangerzone"] = "Запретная зона",
    ["autosentry"] = "Турель",
    ["fists"] = "Кулаки",
    ["tablet"] = "Планшет",
    ["breachcharge"] = "Пробивной заряд",
    ["bumpmine"] = "Толчковая мина",
    ["axe"] = { name = "Топор", desc = "Тяжёлый подручный топор, заточенный с одной стороны. Основная атака — взмах с небольшим уроном, а дополнительная — мощный бросок. Скоро нарубят дроворубы сыры дубы на срубы?" },
    ["hammer"] = { name = "Молоток", desc = "Отлично подходящий как для ремонта, так и для разрушений молоток. Основная атака — взмах с небольшим уроном, а дополнительная — мощный бросок. Когда в руках только молоток, всё вокруг превращается в гвозди." },
    ["spanner"] = { name = "Гаечный ключ", desc = "Обычно такой ключ нужен для создания вращающего момента при закручивании. Теперь же его основная атака — взмах с небольшим уроном, а дополнительная — мощный бросок. Принести гаечный ключ на перестрелку — залог неудачи." },
    ["tripwirefire"] = "Огненная мина",
    ["knife_cord"] = { name = "Паракорд-нож", desc = "Этот нож для выживания с фиксированным клинком можно использовать и как оружие, и как инструмент — например, для установки силков, охоты на животных и срезания веток. Рукоятка обёрнута в универсальный шнур-паракорд." },
    ["knife_canis"] = { name = "Нож выживания", desc = "Зазубренное лезвие этого многофункционального тактического ножа способно пробить жёсткие материалы наподобие кости и плоти, а острый крюк поможет при разделке. Рукоятка из композитного материала прикручена к лезвию шестигранными болтами." },
    ["knife_ursus"] = { name = "Медвежий нож", desc = "Этот нож для выживания в стиле танто отличается огранённым лезвием с цельным вырезом и крепкой рукоятью. Никаких сложностей, никаких двигающихся частей — просто надёжный нож, готовый резать." },
    ["knife_gypsy_jackknife"] = { name = "Наваха", desc = "Этот нож-бабочка с полированной мраморной ручкой прячет маленькое, но очень злое лезвие." },
    ["knife_outdoor"] = { name = "Нож «Бродяга»", desc = "Эргономичный тактический охотничий нож с пружинным механизмом и композитными вставками в рукоятке. Прочное широкое лезвие пригодится для рубки и отделения материалов." },
    ["knife_widowmaker"] = { name = "Коготь", desc = "Этот керамбит с рукоятью из слоновой кости, медными заклёпками и зубчатым лезвием режет на входе и раздирает на выходе." },
    ["knife_stiletto"] = { name = "Стилет", desc = "Cтилет — автоматический складной нож в итальянском стиле, который исторически стал предметом споров и даже запретов; его лезвие практически мгновенно выбрасывается при нажатии кнопки-качельки." },
    ["knife_skeleton"] = { name = "Скелетный нож", desc = "Рукоять этого скелетонизированного ножа для удобства обмотали изолентой. Отверстие нужно для того, чтобы в неё можно было продеть палец: как для удобства, так и для безопасности." },
    ["knife_kukri"] = { name = "Кукри", desc = "Используется в традиционных ритуалах вроде свадеб и поножовщин." },
    ["firebomb"] = { name = "Коктейль Молотова", desc = "Коктейль Молотова — это мощное экспериментальное оружие, которое при броске воспламеняет землю, обжигая игроков в большой области." },
    ["diversion"] = { name = "Диверсионное устройство", desc = "Диверсионное устройство подсвечивает сектор на планшетах, как будто там находится враг." },
    ["frag_grenade"] = { name = "Осколочная граната", desc = "Экспериментальная осколочная граната наносит огромный взрывной урон в большой области, поэтому пригодится как в атаке, так и в защите." },
    ["shield"] = { name = "Полицейский щит", desc = "Защитное приспособление, способное отразить или поглотить урон от огнестрельного оружия и защитить владельца от снарядов самого разного калибра." },
    ["zone_repulsor"] = { name = "Защитное устройство", desc = "Прототип Устройства Радиальной Проекции Защитных Частиц" },
    ["glock_use"] = "• Смертельный вблизи, не страшнее комара вдали\n• Подходит для первого раунда половины\n• Сложность: <color=191,191,64>★★</color>\n\n",
    ["hkp2000_use"] = "• Точный на дальней дистанции, достаточно скорострельный для ближней\n• Подходит для первого раунда половины\n• Сложность: <color=191,191,64>★★</color>\n\n",
    ["usp_silencer_use"] = "• Точный, бесшумный и (до некоторой степени) смертоносный\n• Подходит для первого раунда половины\n• Сложность: <color=191,191,64>★★</color>\n\n",
    ["elite_use"] = "• Два скорострельных пистолета по цене одного\n• Подходят для первого раунда половины\n• Сложность: <color=191,191,64>★★</color>\n\n",
