csstring = { -- Ukrainian
	["cat_pistols"] = "Пістолети",
	["cat_heavy"] = "Важка зброя",
	["cat_smg"] = "Пістолети-кулемети",
	["cat_rifles"] = "Гвинтівки",
	["cat_grenades"] = "Гранати",
	["cat_gear"] = "Спорядження",

    ["ak47"] = { name = "АК-47", desc = "Потужний і надійний, автомат Калашникова — один із найпопулярніших у світі. Найсмертельніший під час стрільби короткими прицільними серіями." },
    ["aug"] = { name = "АУГ", desc = "Потужна і точна гвинтівка з оптичним прицілом АУГ компенсує своє тривале перезаряджання низьким відхиленням куль та високою швидкістю стрільби." },
    ["awp"] = { name = "АВП", desc = "Високі ризики й висока нагорода — сумно відома снайперська гвинтівка АВП легко впізнається за характерним звуком та принципом «один постріл, один труп»." },
    ["deserteagle"] = { name = "Пустельний орел", desc = "Такий же дорогий, як і потужний, «Пустельний орел» — це знаковий пістолет, важкий для опанування, але на диво точний на дальніх дистанціях." },
    ["deagle"] = "Пустельний орел",
    ["elites"] = { name = "Подвійні «Беретти»", desc = "Вогонь одночасно з двох пістолетів «Беретта» з великим магазином зменшує точність та збільшує час заряджання. З іншого боку, ви можете стріляти одразу двома." },
    ["elite"] = "Подвійні «Беретти»",
    ["famas"] = { name = "ФАМАС", desc = "Дешевий варіант для небагатих гравців, ФАМАС гарно заповнює нішу між дорожчими гвинтівками та менш ефективними пістолетами-кулеметами." },
    ["fiveseven"] = { name = "Five-seveN", desc = "Високоточний та бронебійний, дорогий Five-seveN заряджається повільно, однак це компенсується щедрим магазином на 20 патронів та несильною віддачею." },
    ["flashbang"] = { name = "Світлошумова граната", desc = "Несмертельна світлошумова граната тимчасово засліплює будь-кого, хто опинився в зоні ураження, що робить її ідеальною для використання в замкненому просторі. Гучний вибух такої гранати також тимчасово маскує звук від ходіння." },
    ["g3sg1"] = { name = "Г3-СГ1", desc = "Г3-СГ1 коштує багато грошей і значно знижує швидкість руху, але компенсує це більшою швидкострільністю, ніж у інших снайперських гвинтівок." },
    ["galilar"] = { name = "Ґаліл", desc = "Найдешевший із-поміж доступних лише терористам автоматів, «Ґаліл» — це зручна зброя на середніх та великих дистанціях." },
    ["glock18"] = { name = "Ґлок-18", desc = "Пістолет «Ґлок-18» підійде для першого раунду. Він може стріляти серіями з трьох пострілів і буде найбільш ефективним проти суперників без бронежилета." },
    ["he_grenade"] = { name = "Осколкова граната", desc = "Фугасна осколкова граната завдає сильні ушкодження у великій зоні, що робить її ідеальною для зачистки кімнат із ворогами." },
    ["he_grenade_short"] = "Осколкова",
    ["hegrenade"] = "Осколкова граната",
    ["knife"] = { name = "Ніж", desc = "Тихий і завжди доступний бойовий ніж має головну швидку атаку з невеликою кількістю ушкоджень і додаткову повільну з сильними пошкодженнями. Атака зі спини дозволить тихим гравцям миттєво вбити ворога." },
    ["knife_t"] = "Ніж",
    ["knife_gg"] = "Золотий ніж",
    ["knife_ghost"] = { name = "Спектральний ніж", desc = "Привиди люблять примарні ножі. Коли вони їх роздобудуть, стережіться! Вони спробують заколоти вас за першої нагоди. Проте вони лише привиди і їхні ножі не завдають шкоди людям…" },
    ["m249"] = { name = "М249", desc = "Міцний кулемет М249 призначений для відкритого простору й ідеально підходить гравцям, які готові обміняти повільний темп вогню на підвищену влучність та місткий магазин." },
    ["m4a1"] = "М4А4",
    ["m4_silencer"] = "М4А1-С",
    ["m4a1_silencer"] = { name = "М4А1-С", desc = "З меншим магазином, аніж його аналог без глушника, безшумний карабін М4А1 забезпечує тиху стрільбу з меншою віддачею та вищою точністю." },
    ["mac10"] = { name = "МАК-10", desc = "Пістолет-кулемет МАК-10 є, по суті, коробкою, з якої вилітають кулі. Він дуже швидкострільний, однак плата за це — низька точність та сильна віддача." },
    ["p90"] = { name = "П90", desc = "Легко впізнаваний за унікальним дизайном булпапа, П90 — це чудова зброя для стрільби в русі завдяки високій місткості магазина та малій віддачі." },
    ["smoke_grenade"] = { name = "Димова граната", desc = "Димова граната створює димову завісу середнього радіусу. Її можна використати, щоби сховати вашу команду від снайперів, чи просто як відволікаючий маневр." },
    ["smokegrenade"] = "Димова граната",
    ["ump45"] = { name = "УМП-45", desc = "Гидке каченя в родині пістолетів-кулеметів, УМП45, має єдиний недолік – невеликий магазин. Загалом же це чудова універсальна автоматична зброя для ближнього бою." },
    ["xm1014"] = { name = "М1014", desc = "М1014 — потужна й повністю автоматична рушниця, що виправдовує свою чималу ціну можливістю швидко наповнити свинцем будь-яке приміщення." },
    ["c4"] = { name = "Вибухівка", desc = "Стабільний та стійкий до більшості механічних ударів, цей саморобний вибуховий пристрій після запалювання має швидкість детонації понад 8000 метрів за секунду." },
    ["bizon"] = { name = "ПП «Бізон»", desc = "Пістолет-кулемет «Бізон» завдає малих пошкоджень, але його особливістю є унікальний місткий барабанний магазин, що швидко перезаряджається." },
    ["mag7"] = { name = "МАГ-7", desc = "Ексклюзивний для спецпризначенців МАГ-7 завдає приголомшливих ушкоджень із близької відстані. Швидке перезаряджання магазина робить його чудовим тактичним вибором." },
    ["negev"] = { name = "Неґев", desc = "«Неґев» — це звір, що може утримувати ворога в укритті завдяки своєму точковому вогню на пригнічення. Звісно, якщо ви маєте достатньо часу, аби взяти його спершу неточну стрільбу під контроль." },
    ["sawedoff"] = { name = "Обріз", desc = "Класичний обріз завдає вкрай тяжких ушкоджень із близької відстані, однак стрільба з нього неточна й характеризується високим розсіюванням та низькою швидкострільністю. Тож вам краще швидше вбити те, у що ви поцілили." },
    ["tec9"] = { name = "ТЕК-9", desc = "ТЕК-9 ідеально пасує терористам, які активно пересуваються. Він смертоносний у ближньому бою і має місткий магазин." },
    ["taser"] = { name = "Зевс 27", desc = "Однозарядний «Зевс 27» ідеальний для засідок з близької відстані та сутичок у закритому просторі. Він здатен нейтралізувати ворога одним розрядом." },
    ["molotov"] = { name = "Коктейль Молотова", desc = "Коктейль Молотова — це потужна і непередбачувана зброя, яка після падіння розливається полум’ям по невеликій території, вражаючи всіх гравців у своєму радіусі." },
    ["incgrenade"] = "Запалювальна граната",
    ["incgrenade_short"] = "Запалювальна",
    ["decoy"] = "Імітаційна граната",
    ["decoy_short"] = "Імітаційна",
    ["hkp2000"] = { name = "П2000", desc = "Точний і передбачуваний, виготовлений у Німеччині пістолет П2000 надається в першому раунді. Найкраще застосовувати проти ворогів без бронезахисту." },
    ["mp7"] = { name = "МП7", desc = "Багатофункціональний, однак дорогий, німецький пістолет-кулемет МП7 — це найкращий вибір для активних сутичок на короткій дистанції." },
    ["mp5sd"] = { name = "МП5-СД", desc = "Його часто імітували, але так і не змогли повторити. Легендарний МП5, імовірно, найбільш універсальний та популярний пістолет-кулемет у світі. Цей варіант має інтегрований глушник, що робить цю грізну зброю тихою, мов шепіт." },
    ["mp9"] = { name = "МП9", desc = "Вироблений у Швейцарії передовий пістолет-кулемет МП9 — це ергономічна полімерна зброя, яку полюбляють приватні охоронні фірми." },
    ["nova"] = { name = "Нова", desc = "Бюджетність дробовика «Нова» робить його поширеним вибором небагатої команди для нападу із засідки." },
    ["p250"] = { name = "П250", desc = "Швидкострільний пістолет П250 має низьку віддачу і є відносно недорогим вибором проти захищених бронежилетами опонентів." },
    ["cz75"] = "ЧЗ-75 авто",
    ["scar20"] = { name = "СКАР-20", desc = "СКАР-20 — напівавтоматична снайперська гвинтівка, що має високу швидкострільність та завдає значних ушкоджень на великих дистанціях. Однак вона дорога й суттєво знижує швидкість руху." },
    ["sg556"] = "СГ 553",
    ["ssg08"] = { name = "ССГ 08", desc = "ССГ 08 — це снайперська гвинтівка з рухомим затвором, котра не завдає великих ушкоджень, однак має прийнятну ціну. Вона є гарним вибором у перших раундах для влучного вогню з далекої дистанції." },
    ["heavyassaultsuit"] = "Штурмовий бронекостюм",
    ["assaultsuit"] = "Бронежилет і шолом",
    ["assaultsuit_only_helmet"] = "Шолом",
    ["kevlar"] = "Бронежилет",
    ["defuser"] = "Саперний набір",
    ["cutters"] = "Рятувальний набір",
    ["revolver"] = { name = "Револьвер Р8", desc = "Револьвер Р8 стріляє дуже точно та сильно, але сумнозвісний він своїм повільним спусковим гачком. Якщо ворог близько — бийте по гачку якомога швидше." },
    ["usp_silencer"] = { name = "УСП-С", desc = "Улюбленець гравців у Counter-Strike: Source, беззвучний пістолет УСП має від’єднувальний глушник, який дозволяє здійснювати постріли з малою віддачею та без зайвого шуму." },
    ["knifebayonet"] = { name = "Штик-ніж", desc = "Його дизайн суттєво не змінювався з часів Другої світової війни, але штик-ніж усе ще зберігає місце в сучасній військовій стратегії. Він лишався ефективним у нещодавніх війнах у Перській затоці та Афганістані." },
    ["knifeflip"] = { name = "Складаний ніж", desc = "Спортивні складані ножі перського стилю зі стрілоподібним лезом та дуже гострим заточенням. Хоча саме лезо може бути слабким, але загальний дизайн складаного ножа на диво міцний." },
    ["knifegut"] = { name = "Білувальний ніж", desc = "Найпримітнішою особливістю білувального ножа є гак на зворотній стороні леза. Спочатку застосовувався як допоміжний засіб зняття шкіри у тварин, але не менш ефективний для прорізування волокнистих матеріалів, як-от мотузок, тканих стрічок чи ременів безпеки." },
    ["knifecss"] = { name = "Класичний ніж", desc = "Класика серії ігор Counter-Strike. Край цього ножа — пресований стеліт, який чудово тримається завдяки гармонійному поєднанню з титаном на боці та вигині леза. Рукоятка створена зі скам’янілої кістки мамонта і тримається в кишені з вуглецевого волокна." },
    ["knifem9"] = { name = "Штик-ніж М9", desc = "Це американський штик-ніж М9. Спершу призначався для встановлення на гвинтівці, але добре підходить і для ближнього бою." },
    ["knifekaram"] = { name = "Керамбіт", desc = "Керамбіт — ніж із вигнутим клинком, що нагадує пазур тигра. Розроблений як частина бойового мистецтва сілат, що розповсюджене в південно-східній Азії. Зазвичай ніж тримають зворотною хваткою, просовуючи вказівний палець у спеціальне кільце на руків’ї." },
    ["knifetactical"] = { name = "Мисливський ніж", desc = "Ніж, створений для сучасних тактичних вимог, лезо якого гарно підходить і для бойових, і для практичних потреб. Унікальний наконечник у стилі меча танто дає можливість здійснювати максимальне проникнення навіть крізь найміцніші поверхні." },
    ["knife_butterfly"] = { name = "Балісонг", desc = "Це спеціально розроблений балісонг, також відомий як ніж-метелик. Визначальною характеристикою цієї зброї є легке розкриття вільного в обертанні леза віялоподібним рухом, що дозволяє швидко оголити та приховати лезо. Через це ножі-метелики заборонені законами багатьох країн." },
    ["partypopper"] = "Хлопавка",
    ["knife_falchion_advanced"] = { name = "Ніж-фальшіон", desc = "Цей ніж з оберненою кривизною обуха, сучасне переосмислення меча-фальшіона, має характерне загнуте вістря." },
    ["knife_push"] = { name = "Тіньові ножі", desc = "Створені ефективними та брутальними, адже завдавати ударів такими ножами просто як кулаками." },
    ["knife_survival_bowie"] = { name = "Ніж Бові", desc = "Цей цілісний зазубрений ніж Бові створений для виживання у важких екстремальних умовах." },
    ["healthshot"] = "Лікувальна ін’єкція",
    ["tagrenade"] = "Відстежувальна граната",
    ["exploding_barrel"] = "Вибухова бочка",
    ["snowball"] = "Сніжка",
    ["stompdamagekillstring"] = "розбиває вам череп",
    ["dangerzone"] = "Небезпечна зона",
    ["autosentry"] = "Автоматична турель",
    ["fists"] = "Кулаки",
    ["tablet"] = "Планшет",
    ["breachcharge"] = "Пробивний заряд",
    ["bumpmine"] = "Відштовхувальна міна",
    ["axe"] = { name = "Сокира", desc = "Сильна й завжди доступна сокира має як гостру, так і тупу сторону. Ви можете змахнути нею й завдати малої шкоди, або ж кинути й завдати великої шкоди. Якщо не знайдете деревини для рубання, вороги підійдуть також." },
    ["hammer"] = { name = "Молоток", desc = "Молотком можна як лагодити, так і руйнувати речі. Ви можете бити ним і завдавати невеликої шкоди, або ж кинути й завдати великої шкоди. Якщо ви маєте лише молоток, то все навколо здаватиметься цвяхами." },
    ["spanner"] = { name = "Гайковий ключ", desc = "Гайковий ключ зазвичай використовується для закручування чи відкручування предметів. Однак ви можете бити ним і завдавати невеликої шкоди, або ж кинути й завдати великої шкоди. Хочете зганьбитися — принесіть гайковий ключ на перестрілку." },
    ["tripwirefire"] = "Граната-пастка",
    ["knife_cord"] = { name = "Тактичний ніж", desc = "Цей ніж для виживання із зафіксованим лезом створений, щоби слугувати як зброєю, так і інструментом, як-от для встановлення пасток, полювання на тварин чи обтинання листя. Руків’я обгорнене довгим універсальним паракордом." },
    ["knife_canis"] = { name = "Ніж для виживання", desc = "Цей універсальний тактичний ніж має зубчатий край для розрізання грубих матеріалів, як-от кістка чи волокно, а також гострий гак для патрання. Композитний матеріал руків’я прикріплений до леза шестигранними гайками." },
    ["knife_ursus"] = { name = "Ведмежий ніж", desc = "Тантоподібний ніж для виживання має грановане лезо, широкий хвостовик, а також руків’я для завдання ударів. Немає зайвих дрібниць, немає рухомих частин – просто надійне лезо, готове до роботи." },
    ["knife_gypsy_jackknife"] = { name = "Наваха", desc = "Цей складаний ніж з емальованою ручкою приховує маленьке, але смертельно загострене лезо." },
    ["knife_outdoor"] = { name = "Польовий ніж", desc = "Цей ергономічний тактичний мисливський ніж із зафіксованим лезом має композитне руків’я, що вставлене в широке й міцне лезо, зручне для розрізання та проштрикування." },
    ["knife_widowmaker"] = { name = "Ніж-кіготь", desc = "Цей керамбіт із руків’ям зі слонової кістки має латунні заклепки та гострі, мов кігті, гребні, які ріжуть при входженні та розривають при виведенні леза." },
