	["shotgun"] = "Пушка",
	["ak47"] = { name = "AK-47", desc = "Мощен и надежден, AK-47 е един от най-популярните автомати в света. Той е най-смъртоносен с кратки, контролирани откоси при стрелба." },
	["aug"] = { name = "AUG", desc = "Мощен и точен автоматът с оптика, AUG, компенсира за своето дълго време на презареждане с ниско разпръскване и високо скоростна стрелба." },
	["awp"] = { name = "AWP", desc = "Високорисково, но с голяма отплата, емблематичното AWP се разпознава по своята уникална слава, както и тенденцията му за един изстрел, едно убийство." },
	["deserteagle"] = { name = "Desert Eagle", desc = "Толкова скъп, колкото е и мощен, Desert Eagle е пистолет с уникално излъчване, който се овладява трудно, но е изненадващо точен на далечно разстояние." },
	["deagle"] = "Desert Eagle",
	["elites"] = { name = "Dual Berettas", desc = "Стреляйки едновременно две Beretta с големи пълнители, точността Ви намалява и времето за презареждане се увеличава. Добрата новина е, че ще получите възможността да стреляте едновременно с две Beretta, които имат големи пълнители." },
	["elite"] = "Dual Berettas",
	["famas"] = { name = "FAMAS", desc = "Евтин вариант за финансово затруднените играчи, FAMAS ефективно запълва нишата между по-скъпите винтовки и по-малко ефикасните автомати." },
	["fiveseven"] = { name = "Five-SeveN", desc = "Изключително точен и бронебоен, скъпият Five-Seven се зарежда бавно, но компенсира щедро с 20 патрона в пълнителя и умерен откат." },
	["flashbang"] = { name = "Заслепяваща граната", desc = "Несмъртоносната заслепяваща граната временно ослепява всички в рамките на взрива, който причинява, а това я прави идеална за прочистване затворени пространства. Шумната ѝ експлозия, също така временно прикрива звуците от стъпки." },
	["g3sg1"] = { name = "G3SG1", desc = "Скъпичкият G3SG1 значително намалява скоростта на движение, но компенсира с по-висока скорост на стрелба спрямо други снайперистки винтовки." },
	["galilar"] = { name = "Galil AR", desc = "По-евтин вариант измежду ексклузивните автомати за терористите, Galil AR е функционално оръжие в битките при средно и дълго разстояние." },
	["glock18"] = { name = "Glock-18", desc = "Glock 18 е функционален пистолет през първия кръг, който работи най-добре срещу опоненти без бронежилетки и е способен да изстрелва откоси от по три изстрела." },
	["he_grenade"] = { name = "Високоексплозивна граната", desc = "Експлозивната фрагментираща се граната нанася големи щети покриващи широка площ, което я прави идеална за разчистване враждебни стаи." },
	["knife"] = { name = "Нож", desc = "Knife\n \nТих и винаги достъпен, ножът има бърза първична атака за малко щети и бавна вторична, която нанася повече. Наръгването в гръб ще спечели на тихите играчи мигновено убийство." },
	["knife_t"] = { name = "Нож", desc = "Knife\n \nТих и винаги достъпен, ножът има бърза първична атака за малко щети и бавна вторична, която нанася повече. Наръгването в гръб ще спечели на тихите играчи мигновено убийство." },
	["knife_gg"] = "Златен нож",
	["knife_ghost"] = { name = "Спектралното мушкало", desc = "Spectral Shiv\n \nПризраците обичат призрачни ножове. Когато се докопат до такъв, внимавайте! Ще Ви наръгат при първата отдала им се възможност. Жалко, че са призраци, а призрачните ножове не могат да нараняват хора…" },
	["m249"] = { name = "M249", desc = "Лека картечница, уместна за открити пространства, M249 е идеалният избор за играчи, желаещи да заменят бавната скорост на стрелба с по-голяма точност и висок капацитет от муниции." },
	["m4a1"] = "M4A4",
	["m4_silencer"] = "M4A1-S",
	["m4a1_silencer"] = { name = "M4A1-S", desc = "С по-малък пълнител от незаглушеното си съответствие, M4A1 със заглушител предоставя по-тихи изстрели с по-слаб откат и по-добра точност." },
	["mac10"] = { name = "MAC-10", desc = "Макар и най-общо казано да е кутия, от която излизат куршумите, автомата MAC-10 разполага с високо скоростна стрелба, но за сметка на това с лош обхват за точност и силен откат." },
	["p90"] = { name = "P90", desc = "Лесно разпознаваем, заради уникалния си булпъп дизайн, P90 е страхотно оръжие, за стрелба в движение благодарение на своя пълнител с висок капацитет и слаб откат." },
	["smoke_grenade"] = { name = "Димна граната", desc = "Димната граната създава завеса от пушек на средно голяма площ. Тя може ефективно да скрие отбора Ви от снайперисти или просто да създаде успешно отвличане на вниманието." },
	["ump45"] = { name = "UMP-45", desc = "Криворазбраното средно дете от семейството автоматични оръжия, малкият пълнител за UMP45 е единственият недостатък на иначе универсалния автомат при близки разстояния." },
	["xm1014"] = { name = "XM1014", desc = "XM1014 е мощна автоматична пушка, която оправдава по-голямата си цена с възможността бързо да обрисувате дадена стая с олово." },
	["c4"] = { name = "ЕКСПЛОЗИВ C4", desc = "Стабилен и устойчив на повечето физични взаимодействия, този импровизиран пластичен експлозив има скорост на детонация от над 8 000 метра в секунда, когато е възпламенен." },
	["bizon"] = { name = "PP-Bizon", desc = "SMG Bizon нанася малко щети, но предлага уникално проектирани цилиндрични пълнители с голям капацитет, които се презареждат бързо." },
	["mag7"] = { name = "MAG-7", desc = "Ексклузивният за КТ Mag-7 предоставя опустошително количество щети от близко разстояние. Бързият му начин на презареждане с пълнител го прави страхотен тактически избор." },
	["negev"] = { name = "Negev", desc = "Negev е звяр, който може да отблъсне враговете със своята точна потискаща стрелба, в случай, че разполагате с времето да поемете контрола над него." },
	["sawedoff"] = { name = "Скъсена цев", desc = "Класическата пушка със скъсена цев нанася много тежки щети от близко разстояние, но с ниската си точност, широко разпръскване и нискоскоростна стрелба, ще е добре да убиете това, в което се целите." },
	["tec9"] = { name = "Tec-9", desc = "Идеален пистолет за подвижния терорист, Tec-9 е смъртоносен отблизо и разполага с високо капацитетен пълнител." },
	["taser"] = { name = "Zeus x27", desc = "Перфектен за засади от близко разстояние и дуели в затворени пространства, с единичния си изстрел x27 Zeus е в състояние да възпрепятства врага Ви при попадение." },
	["molotov"] = { name = "Молотов", desc = "Коктейлът „Молотов“ е мощно и непредсказуемо оръжие за отказване на достъпа до дадена площ, което избухва в пламъци, когато се хвърли на земята, като наранява всеки играч в неговия радиус." },
	["incgrenade"] = "Възпламенителна граната",
	["decoy"] = "Граната-примамка",
	["hkp2000"] = { name = "P2000", desc = "Точен и контролируем, произведеният в Германия P2000, е функционален пистолет през първия кръг, който работи най-добре срещу опоненти без бронежилетки." },
	["mp7"] = { name = "MP7", desc = "Всестранен, но и скъп, произведеният в Германия автомат MP7 е перфектният избор за силно въздействие при битки на близко разстояние." },
	["mp5sd"] = { name = "MP5-SD", desc = "Често имитиран, но без никакъв еквивалент, емблематичният MP5 вероятно е най-всестранно приложимият и популярен автомат в света. Този SD вариант идва екипиран с вграден заглушител, правейки едно вече застрашително оръжие шептящо тихо." },
	["mp9"] = { name = "MP9", desc = "Произведен в Швейцария, модерния автомат MP9 е ергономично полимерно оръжие предпочитано от частните охранителни фирми." },
	["nova"] = { name = "Nova", desc = "Безобразно ниската цена на пушката Nova я прави страхотно оръжие при засади за финансово затруднен отбор." },
	["p250"] = { name = "P250", desc = "Ниско откатно и скорострелно огнестрелно оръжие, P250 е сравнително евтин избор срещу опоненти с бронежилетки." },
	["cz75"] = "CZ75-Auto",
	["scar20"] = { name = "SCAR-20", desc = "SCAR-20 е полуавтоматична снайперистка винтовка, която заменя високоскоростната стрелба и мощните щети при големи разстояния за бавна скорост на движение и висока цена." },
	["sg556"] = "SG 553",
	["ssg08"] = { name = "SSG 08", desc = "SSG08 нанася малко щети, но е много рентабилна снайперистка винтовка с подвижен затвор, което я прави добър избор за точна стрелба на далечни разстояния през началните кръгове." },
	["heavyassaultsuit"] = "Тежък щурмови костюм",
	["assaultsuit"] = "Бронежилетка + каска",
	["kevlar"] = "Бронежилетка",
	["defuser"] = "Сапьорски клещи",
	["cutters"] = "Спасителна екипировка",
	["revolver"] = { name = "Револвер R8", desc = "R8 Revolver\n \nРеволверът R8 нанася много точен и мощен изстрел за сметка на бавно задействащия се спусък. Може би най-добрият вариант за бърза стрелба, е като използвате ударника, когато се изисква възпираща мощ от упор." },
	["usp_silencer"] = { name = "USP-S", desc = "Любим на Counter-Strike Source феновете, USP пистолетът с подвижен заглушител, придаващ по-слаб откат на изстрелите, като същевременно потиска шума, който би привлякъл внимание." },
	["knifebayonet"] = { name = "Щик", desc = "Bayonet\n \nСъс сравнително непроменен дизайна от Втората световна война насам, щикът е запазил мястото си в съвременната военна стратегия. Щурмовете с щикове продължават да бъдат ефективни до неотдавнашни войни като втората в Залива и тази в Афганистан." },
	["knifeflip"] = { name = "Сгъваем нож", desc = "Flip Knife\n \nСгъваемите ножове са заоблени в персийски стил и разполагат с остър връх. Макар и той да е крехък, цялостният дизайн на сгъваемия нож е учудващо издръжлив." },
	["knifegut"] = { name = "Касапски нож", desc = "Gut Knife\n \nНай-забележителна характеристика на ножа за изкормване е куката в горната част на острието. Първоначално популяризира сред ловците, за да спомогне отстраняване на вътрешностите от дивеча, куката също е полезна при лесното разрязване на влакнести материали, като въжета, ремъци или предпазни колани." },
	["knifecss"] = { name = "Класически нож", desc = "Classic Knife\n \nКласика от Counter-Strike поредицата, острието на този нож е от пресован стеллит. То е перфектно придържано на място поради прецизното му прилягане с титана върху страните и основата на острието. Дръжката е фосилизирана кост от бивник на мастодонт, придържана в гънка от въглеродни влакна." },
	["knifem9"] = { name = "M9 щик", desc = "M9 Bayonet\n \nТова е щикът M-9. Първоначално предназначен, за да бъде монтиран върху оръжието, той е също толкова подходящ и при ръкопашна борба." },
	["knifekaram"] = { name = "Карамбит", desc = "Karambit\n \nС извитото си острие имитиращо нокътя на тигър, керамбитът е разработена като част от югоизточно азиатското бойно изкуство силат. Ножът обикновено се използва с обратен захват, като показалеца се поставя в пръстена." },
	["knifetactical"] = { name = "Ловджийски нож", desc = "Huntsman Knife\n \nНож, проектиран за съвременна тактическа употреба, чието острие е добре пригодено за широк диапазон както от бойни, така и от практически потребности. Уникалният танто връх позволява максимално проникване дори и през най-издръжливите повърхности." },
	["knife_butterfly"] = { name = "Нож „Пеперуда“", desc = "Butterfly Knife\n \nТова е специално изработен сгъваем нож, известен като „пеперуда“. Определящата характеристика на това оръжие е въртеливото движение, с което се открива острието, позволявайки бързото му разгръщане или укриване. В резултат, ножовете тип „пеперуда“ са забранени в много страни." },
	["partypopper"] = "Фойерверки и конфети!",
	["knife_falchion_advanced"] = { name = "Нож „Фалчион“", desc = "Falchion Knife\n \nСъвременно преосмисляне на меча „Фалчион“, това стеснено острие има извит ръб." },
	["knife_push"] = { name = "Мрачни кинжали", desc = "Shadow Daggers\n \nПроектирани за ефективна жестокост, използването на бокс-кама е толкова просто, колкото раздаването на няколко юмрука." },
	["knife_survival_bowie"] = { name = "Боуи нож", desc = "Bowie Knife\n \nТози „Боуи“ нож с фиксирана конструкция и назъбен гръб на клина е проектиран за интензивна употреба в сурови, жестоки ситуации за оцеляване." },
	["healthshot"] = "Медицинска инжекция",
	["tagrenade"] = "Граната за тактическа осведоменост",
	["exploding_barrel"] = "Експлодиращ варел",
	["snowball"] = "Снежна топка",
	["stompdamagekillstring"] = "смачка черепа Ви",
	["dangerzone"] = "Опасна зона",
	["autosentry"] = "Автоматична сензорна картечница",
	["fists"] = "Голи ръце",
	["tablet"] = "Таблет",
	["breachcharge"] = "Пробивен заряд",
	["bumpmine"] = "Отблъскваща мина",
	["axe"] = { name = "Брадва", desc = "Axe\n \nТежка и винаги достъпна, брадвата има остра и тъпа страна. Замахвайте с нея за първична атака с малко щети или я хвърлете за вторична, която нанася повече. Колко дърва би могъл да насече дърварят?" },
	["hammer"] = { name = "Чук", desc = "Hammer\n \nЧукът е достъпен за поправяне и чупене на разни неща. Замахвайте с него за първична атака с малко щети или го хвърлете за вторична, която нанася повече. Ако имате само чук, ще Ви се струва, че всичко е гвоздей." },
	["spanner"] = { name = "Гаечен ключ", desc = "Wrench\n \nГаечният ключ обикновено се използва за прилагане на усукване при завъртане на предмети, но с него можете да ударите всичко за първична атака с малко щети или да го хвърлите за вторична, която нанася повече. Носенето на гаечен ключ в престрелка е рецепта за прецакване." },
	["tripwirefire"] = "Огнен капан",
	["knife_cord"] = { name = "Паракорд нож", desc = "Paracord Knife\n \nТози нож за оцеляване с фиксирано острие е проектиран, така че да издържи на използване като оръжие и инструмент. Като например за поставяне на капани, лов на животни или рязане на растения. Дължината на дръжката е увита с многоцелева парашутна корда." },
	["knife_canis"] = { name = "Нож за оцеляване", desc = "Survival Knife\n \nТози многофункционален тактически нож включва назъбен ръб за разкъсване през груби материали, като например кост или нишки, а плюс това има и остра кука за изкормване. Дръжката от композитен материал е завинтена към острието с шестоъгълни гайки." },
	["knife_ursus"] = { name = "Мечешки нож", desc = "Ursus Knife\n \nТози нож за оцеляване в стил „танто“ се отличава с шлифовано острие и пълен заострен край вкаран в дръжката, която става за нанасяне на удар. Без врява. Без движещи се части. Просто надежден нож, който е готов за работа." },
	["knife_gypsy_jackknife"] = { name = "Наваха нож", desc = "Navaja Knife\n \nТози сгъваем нож с мраморно емайлирана дръжка прикрива малко, но ожесточено заострено острие." },
	["knife_outdoor"] = { name = "Номадският нож", desc = "Nomad Knife\n \nТози ергономичен тактически ловджийски сгъваем нож с композитни втулки за дръжката и широко, здраво острие, е полезен за рязане и разкъсване на материали." },
	["knife_widowmaker"] = { name = "Нож нокът", desc = "Talon Knife\n \nТози карамбит с дръжка от слонова кост, включва месингови нитове и трионовидна ивица, така че прорязва, докато се забива и се разкъсва, когато се изважда." },
	["knife_stiletto"] = { name = "Нож стилет", desc = "Stiletto Knife\n \nОт историческа гледна точка това е фокусна точка на раздора и дори регулацията. Този сгъваем нож стилет в италиански стил включва пружинно острие, което може да се разгъне почти незабавно с натискане на освобождаваща предпазна закопчалка." },
	["knife_skeleton"] = { name = "Скелетен нож", desc = "Skeleton Knife\n \nДръжката на този нож с перфорации в острието е била увита с лента за по-добър захват. Големият отвор позволява прокарване на пръст през нея за стабилност и безопасност." },
