	["cat_gear"] = "Udstyr",

	["ak47"] = { name = "AK-47", desc = "AK-47, kraftfuld og pålidelig, er en af de mest populære kamprifler i verden. Den er mest dødelig i korte, kontrollerede salver af skud." },
	["aug"] = { name = "AUG", desc = "Den kraftfulde og nøjagtige AUG-kampriffel med kikkertsigte kompenserer for dens lange genladningstider med en lille spredning og en høj skudhastighed." },
	["awp"] = { name = "AWP", desc = "Den berygtede AWP, med stor risiko og stor gevinst, er genkendelig med dens signaturrapport og ét skud, ét drab-politik." },
	["deserteagle"] = { name = "Desert Eagle", desc = "Desert Eagle, så dyr som den er kraftfuld, er en ikonisk pistol, der er svær at mestre men overraskende præcis på lang afstand." },
	["deagle"] = "Desert Eagle",
	["elites"] = { name = "Dual Berettas", desc = "At affyre to stormagasin-Berettaer på samme tid gør præcisionen mindre og forøger ladetider. På den lyse side får du lov til at affyre to stormagasin-Berettaer på samme tid." },
	["elite"] = "Dual Berettas",
	["famas"] = { name = "FAMAS", desc = "Som en billig mulighed for pengemanglende spillere fylder FAMAS effektivt nichen mellem dyrere rifler og de mindre effektive maskinpistoler." },
	["fiveseven"] = { name = "Five-SeveN", desc = "Den meget præcise og panserbrydende, men dyre, Five-Seven er en langsom lader, der kompenserer med et gavmildt 20-skudsmagasin og tilgivende rekyl." },
	["flashbang"] = { name = "Flashbang", desc = "Den ikke-dødelige flashbang-granat blænder midlertidigt enhver i dens chokeksplosion, hvilket gør den perfekt til at rømme lukkede områder. Dens højlydte eksplosion dækker også midlertidigt over lyden af fodtrin." },
	["g3sg1"] = { name = "G3SG1", desc = "Den dyre G3SG1 gør din bevægelseshastighed betydeligt lavere men kompenserer med en højere skudhastighed end andre snigskytterifler." },
	["galilar"] = { name = "Galil AR", desc = "Som en mindre dyr mulighed blandt de terrorist-eksklusive kamprifler er Galil AR et brugbart våben i mellem- til langsigtet kamp." },
	["glock18"] = { name = "Glock-18", desc = "Glock 18 er en brugbar pistol til første runde, der virker bedst mod upansrede modstandere og er i stand til at affyre tre-skudssalver." },
	["he_grenade"] = { name = "Højeksplosiv granat", desc = "Den højeksplosive fragmentationsgranat uddeler høj skade i et stort område, hvilket gør den ideel til at rydde fjendtlige rum." },
	["knife"] = { name = "Kniv", desc = "Stille og altid til rådighed. Kniven har et hurtigt, lavt-skadende primært angreb og et langsomt, højt-skadende sekundært angreb. Et rygstik giver en stille spiller et øjeblikkeligt drab." },
	["knife_t"] = { name = "Kniv", desc = "Stille og altid til rådighed. Kniven har et hurtigt, lavt-skadende primært angreb og et langsomt, højt-skadende sekundært angreb. Et rygstik giver en stille spiller et øjeblikkeligt drab." },
	["knife_gg"] = "Guldkniv",
	["knife_ghost"] = { name = "Spøgelseskniv", desc = "Spøgelser elsker spøgelsesknive. Så pas på, når de får fingrene i en! De forsøger at stikke dig, når de kan se deres snit til det. Synd, at de er spøgelser, og spøgelsesknive ikke kan skade folk..." },
	["m249"] = { name = "M249", desc = "M249, et LMG, der er stærkt i åbne områder, er det perfekte valg for spillere, der er villige til at overkomme en langsom skudhastighed for forøget nøjagtighed og høj ammunitionskapacitet." },
	["m4a1"] = "M4A4",
	["m4_silencer"] = "M4A1-S",
	["m4a1_silencer"] = { name = "M4A1-S", desc = "Med et mindre magasin end sit udæmpede modstykke, giver den lydløse M4A1 støjsvage skud med mindre rekyl og bedre præcision." },
	["mac10"] = { name = "MAC-10", desc = "MAC-10-maskinpistolen, der egentlig bare er en kasse, som kugler kommer ud af, har en hurtig skudhastighed men med dårlig præcision og høj rekyl til afvejning." },
	["p90"] = { name = "P90", desc = "P90, der nemt kan genkendes med sit unikke bullpup-design, er et godt våben til at skyde, mens man er i bevægelse, pga. dens højkapacitetsmagasin og lave rekyl." },
	["smoke_grenade"] = { name = "Røggranat", desc = "Røggranaten skaber et mellemstort område med røgslør. Den kan effektivt skjule dit hold fra snigskytter eller blot skabe en nyttig distrahering." },
	["ump45"] = { name = "UMP-45", desc = "UMP45, maskinpistol-familiens misforståede mellembarn, har én ulempe som er dens lille magasin, men den er derudover et fleksibelt automatvåben til nærkamp." },
	["xm1014"] = { name = "XM1014", desc = "XM1014 er et kraftfuldt, fuldautomatisk haglgevær, som retfærdiggør sin heftige pris med evnen til hurtigt at fylde et rum med bly." },
	["c4"] = { name = "C4-sprængstof", desc = "Dette improviserede plastiksprængstof, stabilt og resistent mod de fleste fysiske stød, har en eksplosionshastighed på over 8.000 meter i sekundet, når det antændes." },
	["bizon"] = { name = "PP-Bizon", desc = "Bizon-maskinpistolen gør lav skade men tilbyder et unikt designet højkapacitet-tromlemagasin, der genlader hurtigt." },
	["mag7"] = { name = "MAG-7", desc = "Den CT-eksklusive Mag-7 leverer en katastrofal mængde skade ved kort afstand. Dens hurtige magasin-stil-genladninger gør den til et godt taktisk valg." },
	["negev"] = { name = "Negev", desc = "Negev er et monster af et våben, der kan holde fjenderne på afstand med sin præcise dækningsild, forudsat at du har nok tid til at få styr på den." },
	["sawedoff"] = { name = "Sawed-off", desc = "Den klassiske Sawed-off gør meget kraftig skade på korte afstande, men med dens lave præcision, store spredning og langsomme skudhastighed må du hellere dræbe, hvad du rammer." },
	["tec9"] = { name = "Tec-9", desc = "Tec-9, en ideel pistol for terroristen på farten, er dødelig i nærkamp og har en høj magasinkapacitet." },
	["taser"] = { name = "Zeus x27", desc = "Perfekt til baghold ved korte afstande og træfninger i indelukkede områder. Etskudsvåbnet Zeus x27 kan lamme en fjende på én enkelt træffer." },
	["molotov"] = { name = "Molotov", desc = "Molotovcocktailen er et kraftigt og uforudsigeligt område-nægtende våben, der bryder i flammer, når den kastes på jorden, hvilket skader enhver spiller i dens radius." },
	["incgrenade"] = "Brandgranat",
	["decoy"] = "Lokkegranat",
	["hkp2000"] = { name = "P2000", desc = "Den tysklavede P2000, præcis og kontrollerbar, er en brugbar pistol til første runde, der virker bedst mod upansrede modstandere." },
	["mp7"] = { name = "MP7", desc = "Den tysklavede MP7-maskinpistol, fleksibel men dyr, er det perfekte valg til kamp på korte afstande med stor virkning." },
	["mp5sd"] = { name = "MP5-SD", desc = "Ofte imiteret, men aldrig set lignende: Den ikoniske MP5 er måske den mest alsidige og populære maskinpistol i verden. Denne SD-variant er udstyret med en integreret lyddæmper, som gør et allerede formidabelt våben stille som hvisken." },
	["mp9"] = { name = "MP9", desc = "Den banebrydende MP9-maskinpistol, der fremstilles i Schweiz, er et våben af ergonomisk polymer, som mange private sikkerhedsfirmaer foretrækker." },
	["nova"] = { name = "Nova", desc = "Novas bundpris gør den til et godt bagholdsangrebsvåben for et pengemanglende hold." },
	["p250"] = { name = "P250", desc = "P250, et lavrekyl-skydevåben med høj skudhastighed, er et relativt billigt valg mod pansrede fjender." },
	["cz75"] = "CZ75-Auto",
	["scar20"] = { name = "SCAR-20", desc = "SCAR-20 er en halvautomatisk snigskytteriffel, der med en høj skudhastighed og kraftig lang-afstandsskade dog har langsom bevægelseshastighed og høj pris." },
	["sg556"] = "SG 553",
	["ssg08"] = { name = "SSG 08", desc = "SSG08 bolt-action er en lav-skade men meget omkostningseffektiv snigskytteriffel, hvilket gør den et klogt valg til lang-afstandsskydefærdigheder i de tidlige runder." },
	["heavyassaultsuit"] = "Tungangrebsdragt",
	["assaultsuit"] = "Kevlar + hjelm",
	["kevlar"] = "Kevlarvest",
	["defuser"] = "Demonteringssæt",
	["cutters"] = "Redningssæt",
	["revolver"] = { name = "R8-revolver", desc = "R8-revolveren leverer en meget nøjagtig og kraftfuld kugle men er længe om at udføre trykket på aftrækkeren. At skyde hurtigt, ved at slå på slagstiften, kan være den bedste mulighed, når stoppende kraft på klos hold er krævet." },
	["usp_silencer"] = { name = "USP-S", desc = "En af fanfavoritterne fra Counter-Strike: Source, den lyddæmpede USP-pistol har en aftagelig lyddæmper, der giver skud mindre rekyl mens den dæmper opmærksomhedstiltrækkende støj." },
	["knifebayonet"] = { name = "Bajonet", desc = "Bajonetten, der er relativt uændret i sit design siden Anden Verdenskrig, har stadig sin plads i moderne militærstrategier. Bajonetangreb har fortsat været effektive så nyligt som i den anden Golfkrig og krigen i Afghanistan." },
	["knifeflip"] = { name = "Foldekniv", desc = "Foldeknive er prydet med en persiskstilet tilbagefejende klinge med en skarp spids. Mens spidsen i sig selv måske er skrøbeligt, er foldeknivens overordnede design overraskende holdbart." },
	["knifegut"] = { name = "Jagtkniv", desc = "Den mest fremtrædende af jagtknivens funktioner er tarmkrogen på klingens ryg. Selvom den originalt blev populariseret til at hjælpe med at fjerne dyreindvolde under jagt, er tarmkrogen også effektiv til at skære gennem fiberholdigt materiale som reb, stropper eller sikkerhedsbælter med lethed." },
	["knifecss"] = { name = "Klassisk kniv", desc = "A classic of the Counter-Strike series, the edge of this knife is press-fit Stellite perfectly held in place due to its precise fit with the titanium on the blade's cheek and spine. The handle is fossilized mastodon ivory held in a carbon fibre pocket." },
	["knifem9"] = { name = "M9-bajonet", desc = "Dette er M9-bajonetten. Originalt beregnet til at montere på en riffel, men den er også velegnet til nærkamp." },
	["knifekaram"] = { name = "Karambit", desc = "Med dens kurvede klinge, der efterligner en tigers klo, blev karambitten udviklet som del af den sydøstasiatiske kampsport silat. Kniven bruges typisk med et omvendt greb med fingerringen på pegefingeren." },
	["knifetactical"] = { name = "Huntsman-kniv", desc = "En kniv designet til moderne taktisk brug, hvor klingen er velegnet til et sortiment af både kamp- og nyttebehov. Det unikke Tanto-punkt tillader maksimal penetration gennem selv de stærkeste overflader." },
	["knife_butterfly"] = { name = "Butterfly-kniv", desc = "Dette er en specialdesignet balisong, almindeligvis kendt som en butterfly-kniv. De definerende karakteristikker af dette våben er den blæser-lignende åbning af den fritdrejende klinge, hvilket tillader hurtig udfoldning eller skjulning. Som et resultat er butterfly-knive ulovlige i mange lande." },
	["partypopper"] = "Konfettikanon!",
	["knife_falchion_advanced"] = { name = "Falchion-kniv", desc = "Denne slagpunktsklinge, der er en moderne hyldest til et falchion-sværd, har en kurvet kant." },
	["knife_push"] = { name = "Shadow-daggerter", desc = "Designet til effektiv brutalitet, at bruge en skubbedaggert er så simpelt som at slå en gang eller to." },
	["knife_survival_bowie"] = { name = "Bowie-kniv", desc = "Denne savtakkede, fuld-angel Bowie-kniv er designet til hård brug i brutale overlevelsessituationer." },
	["healthshot"] = "Medicinsprøjte",
	["tagrenade"] = "Taktisk afsløringsgranat",
