	["breachcharge"] = "Sprængladning",
	["bumpmine"] = "Hoppemine",
	["axe"] = { name = "Økse", desc = "Heavy and always available, the axe has a sharp side and a dull side. Swing it for low-damage primary attack or throw it for a high-damage secondary attack. How much wood would a woodchuck chuck?" },
	["hammer"] = { name = "Hammer", desc = "The hammer is available for fixing things and breaking things. Swing it for low-damage primary attack or throw it for a high-damage secondary attack. If all you have is a hammer, everything looks like a nail." },
	["spanner"] = { name = "Skruenøgle", desc = "A wrench is typically used to apply torque when turning objects, but you can hit anything with it for low-damage primary attack or throw it for a high-damage secondary attack. Bringing a wrench to a firefight is a recipe for screwing up." },
	["tripwirefire"] = "TrapFire",
	["knife_cord"] = { name = "Paracordkniv", desc = "This fixed-blade survival knife is designed to withstand being used as both a weapon and a tool, such as for setting traps, hunting animals or cutting foliage. The handle is wrapped in a length of multi-purpose paracord." },
	["knife_canis"] = { name = "Overlevelseskniv", desc = "This multi-purpose tactical knife features a serrated edge for ripping through coarse material like bone or fiber, plus a sharp gutting hook. The composite material handle is bolted to the blade with hex nuts." },
	["knife_ursus"] = { name = "Ursus-kniv", desc = "Denne overlevelseskniv i tanto-stil har en facetteret, fuldtangs knivklinge, fuldført med sværknop. Ingen problemer, ingen bevægelige dele – bare et pålideligt blad, der er klar til brug." },
	["knife_gypsy_jackknife"] = { name = "Navaja-kniv", desc = "Denne springkniv med et greb i marmoremalje skjuler en lille, men ondt tilspidset klinge." },
	["knife_outdoor"] = { name = "Nomadekniv", desc = "This ergonomic tactical hunting lock-blade knife features composite handle inserts and a broad, sturdy blade, useful for cutting and prying apart material." },
	["knife_widowmaker"] = { name = "Talon-kniv", desc = "Denne karambitformede kniv med elfenbensgreb har messingnitter og savkanter, så den skærer på vejen ind og river, når den trækkes ud." },
	["knife_stiletto"] = { name = "Stiletto-kniv", desc = "Denne italienske stiletto-springkniv har historisk set været omdrejningspunkt for kontrovers og lovgivning. Den har en fjedermekanisme, der kan skubbe kniven ud på et øjeblik." },
	["knife_skeleton"] = { name = "Skeletkniv", desc = "This skeletonized-tang knife has been taped at the handle for improved grip. The hole allows a finger to be threaded through for stability and safety." },
	["firebomb"] = { name = "Ildbombe", desc = "En Ildbombe er et kraftfuldt eksperimentelt våben, som frigiver en kemisk reaktion ved høj temperatur, der kan brænde enhver inden for dens store eksplosionsradius." },
	["diversion"] = { name = "Afledningsapparat", desc = "Når et afledningsapparat kastes, aktiveres sektorer på tablets og giver illusionen af yderligere fjender." },
	["frag_grenade"] = { name = "Fragmentationsgranat", desc = "Den eksperimentelle fragmentationsgranat kan yde ekstrem eksplosiv skade på et større område, hvilket gør den ideel til både angreb og forsvar." },
	["shield"] = { name = "Beskyttelsesskjold", desc = "Beskyttelsesudstyr til at afværge eller absorbere ballistisk skade og hjælpe med at beskytte brugeren mod projektiler i forskellige kalibre." },
	["zone_repulsor"] = { name = "Afskærmningsenhed", desc = "Prototype af en radial afskærmningsenhed mod partikeludslip" },
	["cz75a"] = "Som en fuld-automatisk variant til CZ75'eren er CZ75-Auto'en endnu et ideelt, kortsigtet valg for at vende situationen og få fat i din fjendes våben. Men med meget lidt ammunition i magasinet, kræves en stærk aftrækkerdisciplin.",
	["m4a4"] = "M4A4, mere præcis men mindre skadende end sit AK-47-modstykke, er den mest populære fuldautomatiske riffel for CT'ere.",
	["sg553"] = "Den terrorist-eksklusive SG553 er et luksusalternativ til AK-47 med sigte til effektiv lang-afstandssammenstød.",
	["decoy_grenade"] = "Når den smides, efterligner lokkegranaten lyden af det kraftigste våben, du bærer, og skaber illusionen af flere støttende styrker.",
	["incindiary_grenade"] = "Når den kastes frigiver brandgranaten en kemisk reaktion ved høj temperatur, der kan brænde enhver inden for dens store eksplosionsradius.",
	["survival_taser"] = "Et våben med ekstremt kort rækkevidde, der kan dræbe med ét skud. Kan også bruges til at åbne våbenkasser.",
	["survival_healthshot"] = "Gendanner en del af dit helbred og giver et kortvarigt hastighedsboost.",
	["survival_shield"] = "Et beskyttelsesskjold, som kan blokere en stor mængde skade, før det ødelægges.",
	["survival_parachute"] = "Kan udløses i fald for at forhindre faldskade.",
	["survival_exojump"] = "Forøger hoppehøjden mærkbart.",
	["survival_c4"] = "Placer et vilkårligt sted på banen for at skabe en stor eksplosion. Kan bruges til at åbne pengeskabe.",
	["survival_dronepilot"] = "Gør dig i stand til at styre droner. Brug tabletten til at vælge en drone, du vil overtage.",
	["survival_exploremoney"] = "Giver ekstra penge for hver udforskede heksagon.",
	["survival_wavemoney"] = "Giver ekstra penge for hver bølge af farezoneudvidelsen, du overlever.",
	["survival_vesthelm"] = "Provides resistance to damage.",
	
