csstring = {
	["cat_pistols"] = "Πιστολια",
	["cat_heavy"] = "Βαρεα οπλα",
	["cat_smg"] = "Υποπολυβολα",
	["cat_rifles"] = "Τουφεκια",
	["cat_grenades"] = "Χειροβομβιδεσ",
	["cat_gear"] = "Εξοπλισμοσ",

	["ak47"] = { name = "AK-47", desc = "Ισχυρό και αξιόπιστο, το AK-47 είναι ένα από τα πιο δημοφιλή τουφέκια στον κόσμο. Είναι το πιο θανάσιμο σε σύντομες, συγκρατημένες βολές." },
	["aug"] = { name = "AUG", desc = "Ισχυρό και ακριβές, το τουφέκι AUG με τηλεφακό αντισταθμίζει το μεγάλο χρόνο γεμίσματός του με μικρή διασπορά και μεγάλη ταχυβολία." },
	["awp"] = { name = "AWP", desc = "Υψηλού κινδύνου αλλά υψηλής ανταπόδωσης, το περιβόητο AWP είναι αναγνωρίσιμο από το χαρακτηριστικό του ήχο και την τακτική του «μία βολή, ένας θάνατος»." },
	["deserteagle"] = { name = "Desert Eagle", desc = "Ακριβό αλλά δυνατό, το Desert Eagle είναι ένα εμβληματικό πιστόλι δύσκολο να το ελέγξεις αλλά εξαιρετικά εύστοχο σε μεγάλες αποστάσεις." },
	["deagle"] = "Desert Eagle",
	["elites"] = { name = "Dual Berettas", desc = "Το να πυροβολείτε ταυτόχρονα με δύο Beretta με μεγάλους γεμιστήρες θα μειώσει την ευστοχία και θα αυξήσει το χρόνο γεμίσματος. Από την αλλή πλευρά, θα πυροβολείτε ταυτόχρονα δύο Beretta με μεγάλους γεμιστήρες." },
	["elite"] = "Dual Berettas",
	["famas"] = { name = "FAMAS", desc = "Μια φθηνή εναλλακτική για οικονομικά πιεσμένους παίκτες, το FAMAS καλύπτει αποτελεσματικά το χάσμα μεταξύ των πιο ακριβών τουφεκιών και των λιγότερο αποτελεσματικών υποπολυβόλων (SMG)." },
	["fiveseven"] = { name = "Five-SeveN", desc = "Εξαιρετικά ακριβές και διατρητικό, το ακριβό Five-Seven γεμίζει αργά αλλά αντισταθμίζει με έναν γενναιόδωρο γεμιστήρα με 20 σφαίρες και επιεική ανάκρουση." },
	["flashbang"] = { name = "Χειροβομβίδα κρότου-λάμψης", desc = "Η μη-θανατηφόρα χειροβομβίδα κρότου-λάμψης τυφλώνει προσωρινά οποιονδήποτε βρεθεί μέσα στην έκρηξη, καθιστώντας την ιδανική για καθαρισμό κλειστών περιοχών. Η δυνατή της έκρηξη καλύπτει επίσης προσωρινά των ήχο των βημάτων." },
	["g3sg1"] = { name = "G3SG1", desc = "Το ακριβό G3SG1 ελαττώνει την ταχύτητα κίνησης σημαντικά αλλά το αντισταθμίζει με μία μεγαλύτερη ταχυβολία σε σχέση με τα υπόλοιπα τουφέκια ακροβολιστή." },
	["galilar"] = { name = "Galil AR", desc = "Μια λιγότερο ακριβή εναλλακτική σε τουφέκια αποκλειστικά για τρομοκράτες, το Galil AR είναι ένα εύχρηστο όπλο για μάχες μετρίων έως μεγάλων αποστάσεων." },
	["glock18"] = { name = "Glock-18", desc = "Το Glock 18 είναι ένα χρήσιμο πιστόλι τριών ριπών για τον πρώτο γύρο που λειτουργεί καλύτερα απέναντι σε αντιπάλους χωρίς αλεξίσφαιρο." },
	["he_grenade"] = { name = "Χειροβομβίδα", desc = "Η χειροβομβίδα προκαλεί πολλή ζημιά σε ευρεία περιοχή, καθιστώντας την ιδανική για να καθαρίζει εχθρικά δωμάτια." },
	["knife"] = { name = "Μαχαίρι", desc = "Αθόρυβο και πάντα διαθέσιμο, το μαχαίρι έχει μια γρήγορη, χαμηλής ζημιάς αρχική επίθεση και μια αργή, υψηλής ζημιάς δευτερεύουσα επίθεση. Ένα πισώπλατο μαχαίρωμα θα παραδώσει στους αθόρυβους παίκτες έναν άμεσο σκοτωμό." },
	["knife_t"] = { name = "Μαχαίρι", desc = "Αθόρυβο και πάντα διαθέσιμο, το μαχαίρι έχει μια γρήγορη, χαμηλής ζημιάς αρχική επίθεση και μια αργή, υψηλής ζημιάς δευτερεύουσα επίθεση. Ένα πισώπλατο μαχαίρωμα θα παραδώσει στους αθόρυβους παίκτες έναν άμεσο σκοτωμό." },
	["knife_gg"] = "Χρυσό μαχαίρι",
	["knife_ghost"] = { name = "Μαχαίρι φάντασμα", desc = "Τα φαντάσματα λατρεύουν τα μαχαίρια φαντάσματα. Όταν αποκτήσουν ένα, προσέξτε! Θα σας μαχαιρώσουν με την πρώτη ευκαιρία που θα βρούνε. Κρίμα που είναι φαντάσματα και τα φαντάσματα δεν μπορούν να βλάψουν ανθρώπους...." },
	["m249"] = { name = "M249", desc = "Ένα δυνατό LMG ανοικτής περιοχής, το M249 είναι η τέλεια επιλογή για παίκτες που διατίθενται να ανταλλάξουν μια χαμηλή ταχυβολία για αυξημένη ευστοχία και μεγάλη χωρητικότητα πυρομαχικών." },
	["m4a1"] = "M4A4",
	["m4_silencer"] = "M4A1-S",
	["m4a1_silencer"] = { name = "M4A1-S", desc = "Με μικρότερο γεμιστήρα από τον μη σιγασμένο ομόλογό του, το σιγασμένο M4A1 παρέχει αθόρυβες βολές με λιγότερη ανάκρουση και καλύτερη ακρίβεια." },
	["mac10"] = { name = "MAC-10", desc = "Ουσιαστικά ένα κουτί από το οποίο βγαίνουν σφαίρες, το MAC-10 SMG έχει υψηλή ταχυβολία, με μικρή ακρίβεια διασποράς και μεγάλη ανάκρουση για αντάλλαγμα." },
	["p90"] = { name = "P90", desc = "Εύκολα αναγνωρίσιμο από το μοναδικό σχεδιασμό του τύπου bullpup, το P90 είναι ένα τέλειο όπλο για να πυροβολείτε εν κινήσει χάρις στον υψηλής χωρητικότητας γεμιστήρα και στην μικρή ανάκρουσή του." },
	["smoke_grenade"] = { name = "Χειροβομβίδα καπνού", desc = "Η χειροβομβίδα καπνού δημιουργεί μια κάλυψη καπνού σε μια περιοχή μεσαίου μεγέθους. Μπορεί να κρύψει αποτελεσματικά την ομάδα σας από ελεύθερους σκοπευτές, ή απλά να δημιουργήσει ένα χρήσιμο αντιπερισπασμό." },
	["ump45"] = { name = "UMP-45", desc = "Το παρεξηγημένο μεσαίο παιδί της οικογένειας των υποπολυβόλων (SMG), ο μικρός γεμιστήρας του UMP45 είναι το μοναδικό ελάττωμα σε ένα κατά τα άλλα ευέλικτο αυτόματο για μάχες εκ του συστάδην." },
	["xm1014"] = { name = "XM1014", desc = "Το XM1014 είναι μια ισχυρή πλήρως αυτόματη καραμπίνα που δικαιολογεί την τιμή της με την ικανότητά της να γεμίζει το δωμάτιο με βλήματα." },
	["c4"] = { name = "Εκρηκτικά C4", desc = "Σταθερό και ανθεκτικό στους περισσότερους φυσικούς κραδασμούς, αυτό το αυτοσχέδιο πλαστικό εκρηκτικό έχει ταχύτητα έκρηξης άνω των 8.000 μέτρων το δευτερόλεπτο όταν αναφλεγεί." },
	["bizon"] = { name = "PP-Bizon", desc = "Το υποπολυβόλο (SMG) Bizon προκαλεί μικρή ζημιά, αλλά προσφέρει ένα μοναδικά σχεδιασμένο κυλινδρικό γεμιστήρα μεγάλης χωρητικότητας που γεμίζει γρήγορα." },
	["mag7"] = { name = "MAG-7", desc = "Το Mag-7, αποκλειστικά για την αντιτρομοκρατική, προκαλεί καταστροφική ζημιά σε κοντινές αποστάσεις. Ο γεμιστήρας του ξαναγεμίζεται γρήγορα, κάνοντας το όπλο αυτό μια τέλεια τακτική επιλογή." },
	["negev"] = { name = "Negev", desc = "Το Negev είναι ένα θηρίο που μπορεί να κρατάει τον εχθρό μακριά με εύστοχα πυρά καταστολής, δεδομένου ότι έχετε την πολυτέλεια του χρόνου για να αποκτήσετε τον έλεγχο πάνω του." },
	["sawedoff"] = { name = "Sawed-Off", desc = "Το κλασικό Sawed-Off προκαλεί τεράστια κοντινή ζημιά, αλλά με τη μικρή ευστοχία του, τη μεγάλη διασπορά και τη χαμηλή ταχυβολία του, καλύτερα να σκοτώσετε αυτό που χτυπάτε." },
	["tec9"] = { name = "Tec-9", desc = "Ένα ιδανικό πιστόλι για τον Τρομοκράτη εν κινήσει, το Tec-9 είναι θανατηφόρο σε κοντινή απόσταση και προσφέρει μεγάλη χωρητικότητα γεμιστήρα." },
	["taser"] = { name = "Zeus x27", desc = "Ιδανικό για ενέδρες κοντινών αποστάσεων και διαπληκτισμούς σε κλειστούς χώρους, το μονής ριπής Zeus x27 είναι ικανό να εξουδετερώσει έναν εχθρό με μία βολή." },
	["molotov"] = { name = "Μολότοφ", desc = "Το Μολότοφ είναι ένα πολύ ισχυρό όπλο που όταν ριφθεί στο έδαφος το τυλίγει απρόβλεπτα στις φλόγες, τραυματίζοντας οποιονδήποτε παίκτη είναι στην εμβέλειά του." },
	["incgrenade"] = "Εμπρηστική χειροβομβίδα",
	["decoy"] = "Χειροβομβίδα παραπλάνησης",
	["hkp2000"] = { name = "P2000", desc = "Ακριβές και ελεγχόμενο, το γερμανικό P2000 είναι ένα χρήσιμο πιστόλι για τον πρώτο γύρο που λειτουργεί καλύτερα απέναντι σε αντιπάλους χωρίς αλεξίσφαιρο." },
	["mp7"] = { name = "MP7", desc = "Πολλαπλών χρήσεων αλλά ακριβό, το Γερμανικής κατασκευής υποπολυβόλο (SMG) MP7 είναι η τέλεια επιλογή για να προκαλέσετε σημαντικές ζημιές σε μάχες κοντινών αποστάσεων." },
	["mp5sd"] = { name = "MP5-SD", desc = "Συχνά το μιμούνται αλλά ποτέ δεν το φτάνουν, το εικονικό MP5 είναι ίσως το προσαρμόσιμο και δημοφιλές SMG στον κόσμο. Η έκδοση SD παρέχει έναν ενσωματωμένο σιγαστήρα, καθιστώντας ένα ήδη τρομερό όπλο αθόρυβο σαν ψίθυρο." },
	["mp9"] = { name = "MP9", desc = "Κατασκευασμένο στην Ελβετία, το πρωτοποριακό υποπολυβόλο (SMG) MP9 είναι ένα εργονομικό όπλο από πολυμερές που προτιμάται από εταιρείες ιδιωτικής ασφάλειας." },
	["nova"] = { name = "Nova", desc = "Η πολύ χαμηλή τιμή του Nova το κάνει το τέλειο όπλο για ενέδρες για μια οικονομικά πιεσμένη ομάδα." },
	["p250"] = { name = "P250", desc = "Ένα πυροβόλο όπλο με μικρή ανάκρουση και μεγάλη ταχυβολία, το P250 είναι μια σχετικά φθηνή επιλογή ενάντια σε θωρακισμένους εχθρούς." },
	["cz75"] = "CZ75-Auto",
	["scar20"] = { name = "SCAR-20", desc = "Το SCAR-20 είναι ένα ημιαυτόματα τουφέκι ακροβολιστή που ανταλλάζει την μεγάλη ταχυβολία του και την ισχυρή ζημιά του σε μεγάλη εμβέλεια για μια μεγάλη μείωση στην ταχύτητα κίνησης και μια αρκετά ακριβή τιμή." },
	["sg556"] = "SG 553",
	["ssg08"] = { name = "SSG 08", desc = "Το SSG08 bolt-action είναι ένα χαμηλής ζημιάς αλλά αποδοτικό για την αξία του τουφέκι ακροβολιστή, που το καθιστά μια έξυπνη επιλογή για σκοποβολή μεγάλης εμβέλειας στους πρώτους γύρους." },
	["heavyassaultsuit"] = "Στολή βαριάς θωράκισης",
	["assaultsuit"] = "Αλεξίσφαιρο + Κράνος",
	["kevlar"] = "Αλεξίσφαιρο γιλέκο",
	["defuser"] = "Σύνεργα αφοπλισμού",
	["cutters"] = "Σύνεργα διάσωσης",
	["revolver"] = { name = "R8 Revolver", desc = "Το R8 Revolver προσφέρει εξαιρετικά ακριβή και δυνατή βολή εις βάρος του χρόνου τραβήγματος της σκανδάλης. Οι γρήγοροι πυροβολισμοί χτυπώντας επανειλημμένα τον κόκορα ίσως είναι η καλύτερη επιλογή όταν απαιτείται μεγάλη δύναμη άμεσα." },
	["usp_silencer"] = { name = "USP-S", desc = "Αγαπημένο από τους οπαδούς του Counter-Strike: Source, το κατασιγασμένο πιστόλι USP έχει έναν αφαιρούμενο σιγαστήρα που δίνει μικρότερη ανάκρουση στις βολές, ενώ ταυτόχρονα καταστέλλει τον θόρυβο." },
	["knifebayonet"] = { name = "Mπαγιονέτα", desc = "Σχετικά αμετάβλητος ο σχεδιασμός της από τον Β' Παγκόσμιο πόλεμο, η μπαγιονέτα ακόμα διατηρεί μια θέση στη σύγχρονη στρατιωτική στρατηγική. Οι χρήσεις της συνέχισαν να είναι αποτελεσματικές όπως πρόσφατα στο Β' Πόλεμο του Κόλπου και στον Πόλεμο του Αφγανιστάν." },
	["knifeflip"] = { name = "Flip Knife", desc = "Αυτό το μαχαίρι έχει μια λεπίδα περσικού τύπου στο πίσω μέρος με οξύ άκρο. Ενώ η μύτη μπορεί να είναι εύθραυστη, ολόκληρος ο σχεδιασμός του μαχαιριού είναι πολύ ανθεκτικός." },
	["knifegut"] = { name = "Gut Knife", desc = "Το πιο εμφανές χαρακτηριστικό αυτού του μαχαιριού είναι ο γάντζος ξεκοιλιάσματος στο κορμό του μαχαιριού. Αρχικά έγινε δημοφιλές σε κυνήγι άγριων ζώων, αλλά είναι επίσης χρήση για κόψιμο διάφορων υλικών όπως σκοινί, ιστούς ή ζώνες ασφαλείας με ευκολία." },
	["knifecss"] = { name = "Κλασικό Μαχαίρι", desc = "Ένα κλασικό της σειράς Counter-Strike, η άκρη αυτού του μαχαιριού εφαρμόστηκε τέλεια στη θέση της με πίεση Stellite χάρη στο ακριβές κόψιμό της με τιτάνιο στο πλάι και ράχη της λεπίδας. Η λαβή είναι απολιθωμένο ελεφαντόδοντο μαστόδοντα που κρατιέται σε θήκη από ανθρακόνημα." },
	["knifem9"] = { name = "Mπαγιονέτα M9", desc = "Αυτή είναι η μπαγιονέτα M-9. Αρχικά προοριζόταν να τοποθετηθεί σε τουφέκι, αλλά είναι επίσης κατάλληλη και για κοντινές μάχες." },
	["knifekaram"] = { name = "Karambit", desc = "Με την κυρτή λεπίδα του να μιμείται το νύχι ενός τίγρη, το karambit αναπτύχθηκε ως κομμάτι της νοτιοανατολικής Ασιατικής πολεμικής τέχνης silat. Το μαχαίρι χρησιμοποιήται τυπικά με αντίστροφη λαβή, με το δαχτυλίδι του στο δείκτη." },
	["knifetactical"] = { name = "Μαχαίρι Huntsman", desc = "Ένα μαχαίρι σχεδιασμένο για σύγχρονες τακτικές χρήσεις, η λεπίδα είναι ιδανική για ένα ευρύ φάσμα αναγκών μάχης και γενικής χρήσης. Η μοναδική αιχμή Tanto επιτρέπει τη μέγιστη δυνατή διείσδυση διαμέσου ακόμα και των σκληρότερων επιφανειών." },
	["knife_butterfly"] = { name = "Μαχαίρι Butterfly", desc = "Το προσδιοριστικό χαρακτηριστικό αυτού του μαχαιριού πεταλούδας είναι πως ανοίγει σαν βεντάλια, έχει ελεύθερο άξονα περιστροφής και επιτρέπει γρήγορη ανάπτυξη ή απόκρυψη. Για αυτό το λόγο είναι απαγορευμένο σε πολλές χώρες." },
	["partypopper"] = "Party Popper!",
	["knife_falchion_advanced"] = { name = "Μαχαίρι Falchion", desc = "Ένας φόρος τιμής σε ένα σπαθί, αυτή η λεπίδα έχει κυρτή άκρη." },
	["knife_push"] = { name = "Shadow Daggers", desc = "Σχεδιασμένο για αποτελεσματική βαναυσότητα, είναι εύχρηστο σαν να ρίχνεις μια γροθιά." },
	["knife_survival_bowie"] = { name = "Μαχαίρι Bowie", desc = "Αυτό το πριονωτό μαχαίρι Bowie είναι σχεδιασμένο για βαριά χρήση σε κτηνώδεις καταστάσεις επιβίωσης." },
	["healthshot"] = "Γιατρικό",
	["tagrenade"] = "Χειροβομβίδα τακτικής αναγνώρισης",
	["exploding_barrel"] = "Εκρηκτικό βαρέλι",
	["snowball"] = "Χιονόμπαλα",
	["stompdamagekillstring"] = "σύνθλιψε το κρανίο σας",
	["dangerzone"] = "Ζώνη Κινδύνου",
	["autosentry"] = "Αυτόματο Ταχυβόλο",
	["fists"] = "Γυμνά χέρια",
	["tablet"] = "Τάμπλετ",
	["breachcharge"] = "Εκρηκτικό διάρρηξης",
	["bumpmine"] = "Νάρκη ώθησης",
	["axe"] = { name = "Τσεκούρι", desc = "Βαρύ και πάντα διαθέσιμο, το τσεκούρι έχει μια κοφτερή και μια στομωμένη πλευρά. Χρησιμοποιήστε το για κύρια επίθεση χαμηλής ζημιάς ή πετάξτε το για δευτερεύουσα επίθεση υψηλής ζημιάς. Πόσο ξύλο θα τσακίσει ένας ξυλοκόπος;" },
	["hammer"] = { name = "Σφυρί", desc = "Το σφυρί είναι διαθέσιμο για να φτιάχνετε πράγματα και να σπάτε πράγματα. Χρησιμοποιήστε το για κύρια επίθεση χαμηλής ζημιάς ή πετάξτε το για δευτερεύουσα επίθεση υψηλής ζημιάς. Αν το μόνο που έχετε είναι ένα σφυρί, όλα σας φαίνονται καρφί." },
	["spanner"] = { name = "Γαλ. κλειδί", desc = "Το γαλλικό κλειδί χρησιμοποιείται συνήθως για εφαρμογή ροπής κατά την περιστροφή αντικειμένων, αλλά μπορείτε να χτυπήσετε οτιδήποτε με αυτό για κύρια επίθεση χαμηλής ζημιάς ή να το πετάξετε για δευτερεύουσα επίθεση υψηλής ζημιάς. Το να φέρετε γαλλικό κλειδί σε πιστολίδι είναι σίγουρος τρόπος να χάσετε βίδες." },
	["tripwirefire"] = "TrapFire",
	["knife_cord"] = { name = "Μαχαίρι Paracord", desc = "Αυτό το μαχαίρι σταθερής λεπίδας σχεδιάστηκε για να αντέχει να χρησιμοποιείται και σαν όπλο και σαν εργαλείο, όπως για να βάζετε παγίδες, να κυνηγάτε ζώα ή να κόβετε φυλλώματα. Η λαβή είναι τυλιγμένη με μακρύ σχοινί αρτάνη πολλαπλών χρήσεων." },
	["knife_canis"] = { name = "Μαχαίρι Survival", desc = "Αυτό το τακτικό μαχαίρι πολλαπλών χρήσεων διαθέτει μια οδοντωτή άκρη για να σχίζετε τραχύ υλικό όπως οστό ή ίνες, συν ένα μυτερό γάντζο ξεκοιλιάσματος. Η λαβή από σύνθετο υλικό είναι καρφωμένη στη λεπίδα με εξάγωνες βίδες." },
	["knife_ursus"] = { name = "Μαχαίρι Ursus", desc = "Αυτό το μαχαίρι επιβίωσης σε στυλ κοντού ιαπωνικού μαχαιριού έχει πολύπλευρη μύτη και πλήρη λεπίδα, μαζί με σθεναρή λαβή. Όχι φασαρία, όχι κινούμενα μέρη, απλά μια έμπιστη λεπίδα που είναι έτοιμη να δουλέψει." },
	["knife_gypsy_jackknife"] = { name = "Μαχαίρι Navaja", desc = "Αυτό το μαχαίρι πεταλούδα με μαρμάρινη σμαλτωμένη λαβή κρύβει μια μικρή αλλά μοχθηρή ακονισμένη λεπίδα." },
