	["awp"] = { name = "AWP", desc = "Suuren riskin, mutta myös suuren palkkion antava, (surullisen)kuuluisa AWP on tunnistettavissa uniikista äänestään ja yksi osuma, yksi tappo -toimintaperiaatteestaan." },
	["deserteagle"] = { name = "Desert Eagle", desc = "Yhtä kallis kuin tehokaskin, Desert Eagle on ikoninen pistooli, joka on vaikea hallita, mutta on hyvinkin tarkka pitkällä matkalla." },
