	["fiveseven"] = { name = "Five-SeveN", desc = "Tarkka ja panssarit läpäisevä Five-Seven on kallis, hitaasti lataava pistooli, jota on kompensoitu 20 panoksen lippaalla ja anteeksiantavalla potkaisulla." },
	["flashbang"] = { name = "Valokranaatti", desc = "Valokranaatti ei tapa, mutta sokaisee hetkellisesti jokaisen iskukantaman alueella olevan, ja on siksi täydellinen vihollisen savustamiseen ulos ahtaista paikoista. Sen kova räjähdys peittää hetkellisesti myös askelten äänet." },
	["g3sg1"] = { name = "G3SG1", desc = "Hintava G3SG1 hidastaa matkantekoasi huomattavasti, mutta ampuu paljon nopeammin kuin muut kiikarikiväärit." },
	["galilar"] = { name = "Galil AR", desc = "Halvempi vaihtoehto terroristien rynnäkkökiväärien joukossa, Galil AR on toimiva ase keskipitkästä pitkään matkaan." },
	["glock18"] = { name = "Glock-18", desc = "Glock 18 on hyödyllinen ensimmäisen kierroksen pistooli, joka pystyy ampumaan kolmen ammuksen sarjoja ja tehoaa parhaiten vihollisiin, joilla ei ole Kevlar-liiviä." },
	["he_grenade"] = { name = "HE-kranaatti", desc = "Voimakas sirpalekranaatti kylvää tuhoa laajalle alueelle, ja on täydellinen suljettujen tilojen tyhjentämiseen vihollisista." },
	["knife"] = { name = "Veitsi", desc = "Taisteluveitsi on hiljainen ja aina saatavilla. Sen tavallinen hyökkäys on nopea mutta heikko, vaihtoehtoinen hyökkäys tappavampi. Hiljaiset pelaajat saavat selkäänpuukottamalla välittömän tapon." },
	["knife_t"] = { name = "Veitsi", desc = "Taisteluveitsi on hiljainen ja aina saatavilla. Sen tavallinen hyökkäys on nopea mutta heikko, vaihtoehtoinen hyökkäys tappavampi. Hiljaiset pelaajat saavat selkäänpuukottamalla välittömän tapon." },
	["knife_gg"] = "Kultaveitsi",
	["knife_ghost"] = { name = "Aaveveitsi", desc = "Kummitukset rakastavat kummitusveitsiä. Ole valppaana, kun ne saavat käsiinsä sellaisen! Ne yrittävät puukottaa ensimmäisen tilaisuuden tultua. Harmi kyllä, ne ovat kummituksia, eikä kummitusveitsetkään pysty vahingoittamaan ihmisiä...." },
	["m249"] = { name = "M249", desc = "Tehokas ja avoimiin tiloihin soveltuva M249-konekivääri on täydellinen pelaajille, jotka haluavat lisää tarkkuutta ja suuremman ammusmäärän tulitusnopeuden kustannuksella." },
	["m4a1"] = "M4A4",
