	["knife_cord"] = { name = "Paracord-kahvainen kenttäpuukko", desc = "Tämä selviytymisveitsi on suunniteltu sekä aseeksi että työkaluksi, ja sillä voi asettaa ansoja, metsästää eläimiä ja raivata lehvistöä. Veitsen kahvaan on kiedottu monikäyttöinen paracord-naru." },
	["knife_canis"] = { name = "Selviytymisveitsi", desc = "Tässä monikäyttöisessä taktisessa puukkossa on terävä koukku sekä sahalaitainen reuna, jonka ansioista sillä voi repiä jämäkämpääkin materiaalia, kuten luita tai kuituja. Sen komposiittikahva on kiinnitetty terään tukevasti kuusiomuttereilla." },
	["knife_ursus"] = { name = "Karhuveitsi", desc = "Tässä tantō-tyylisessä selviytymisveitsessä on viistetty terä ja täysruoto, jotka on viimeistelty iskuponsilla. Ei hössötystä eikä liikkuvia osia - ainoastaan luotettava veitsi, joka on valmis töihin." },
	["knife_gypsy_jackknife"] = { name = "Linkkuveitsi", desc = "Marmorikiillekahvaisessa taittoveitsessä on pieni mutta vaarallinen ja kapea terä." },
	["knife_outdoor"] = { name = "Nomad-puukko", desc = "Tämä ergonominen metsästykseen tarkoitettu kääntöveitsi koostuu komposiittikahvasta sekä leveästä ja tukevasta terästä. Hyödyllinen leikkelyyn ja materiaalin repimiseen." },
	["knife_widowmaker"] = { name = "Kynsiveitsi", desc = "Tässä norsunluukahvaisessa karambit-veitsessä on messinkiniitit ja sahanteräselkä, minkä vuoksi se leikkaa mennessä ja repii tullessa." },
	["knife_stiletto"] = { name = "Stiletti", desc = "Historiassa eripuran ja säännöstelyn keskipisteenä olleessa italialaistyyppisessä stilettiveitsessä on jousitettu terä, jonka saa otettua esiin lähes välittömästi nappia painamalla." },
	["knife_skeleton"] = { name = "Skeleton-puukko", desc = "Skeleton-puukon kahva on teipattu, jotta siitä saa paremman otteen. Reikään voi pujottaa sormen, jolloin ote on vakaampi ja turvallisempi." },
	["firebomb"] = { name = "Polttopullo", desc = "Palopommi on voimakas kokeellinen ase. Heitettäessä se syttyy palamaan, vapauttaa suuren määrän lämpöä ja polttaa sen laajalla räjähdysalueella olevia." },
	["diversion"] = { name = "Harhautuslaite", desc = "Heitetty harhautuslaite korostaa tabletilla sektoreita, mikä luo harhan useammasta vihollisesta." },
	["frag_grenade"] = { name = "Sirpalekranaatti", desc = "Kokeellinen sirpalekranaatti voi aiheuttaa valtavan räjähdysvahingon laajalla alueella. Nämä ominaisuudet tekevät siitä ihanteellisen sekä hyökkäys- että puolustusstrategioille." },
	["shield"] = { name = "Mellakkakilpi", desc = "Suojavaruste, joka on suunniteltu vaimentamaan ja kääntämään ballistinen vahinko pois kohteesta sekä auttamaan suojaamaan kantajaansa ammussateelta." },
