	["ak47"] = { name = "AK-47", desc = "Az erős és megbízható AK-47 az egyik legnépszerűbb gépkarabély a világon. Rövid, célzott sorozattűz leadásakor a leghalálosabb." },
	["aug"] = { name = "AUG", desc = "Az erős és pontos AUG távcsöves gépkarabély hosszú újratöltési idejét kis szórásával és magas tűzgyorsaságával egyenlíti ki." },
	["awp"] = { name = "AWP", desc = "Magas kockázat és magas nyereség; a hírhedt AWP jellegzetes lövéshangjáról, és egy lövés, egy ölés szabályáról ismerhető fel." },
	["deserteagle"] = { name = "Desert Eagle", desc = "A legendás Desert Eagle pisztoly olyan erős, amilyen drága; nehéz a mesterévé válni, de meglepően pontos nagy távolságra." },
	["deagle"] = "Desert Eagle",
	["elites"] = { name = "Dupla Beretta", desc = "Két nagy táras Berettával egyszerre lőve csökken a pontosság és nő az újratöltési idő. A jó hír viszont, hogy egyszerre két nagy táras Berettával fogsz lőni." },
	["elite"] = "Dupla Beretta",
	["famas"] = { name = "FAMAS", desc = "Olcsó lehetőség pénzszűkében levő játékosoknak; a FAMAS hatékonyan tölti ki a rést a drágább karabélyok és a kevésbé hatékony géppisztolyok között." },
	["fiveseven"] = { name = "Five-SeveN", desc = "A nagyon pontos és páncélátütő, borsos árú Five-Seven lassú újratöltésű, amit bőséges, 20 lőszeres tárral és megbocsátható visszarúgással egyenlít ki." },
	["flashbang"] = { name = "Villanógránát", desc = "A nem halálos villanógránát átmenetileg mindenkit elvakít a lökéshullám-területén belül, ami tökéletessé teszi elzárt területek kifüstöléséhez. Hangos robbanása időlegesen elfedi a lépések hangját is." },
	["g3sg1"] = { name = "G3SG1", desc = "A borsos árú G3SG1 jelentősen csökkenti a mozgási sebességet, de kiegyenlíti ezt a többi mesterlövész puskáénál nagyobb tűzgyorsasággal." },
	["galilar"] = { name = "Galil AR", desc = "Kevésbé drága lehetőségként a csak terroristáknak elérhető gépkarabélyok között a Galil AR használható fegyver közepes ás nagy távolságú harchoz." },
	["glock18"] = { name = "Glock-18", desc = "A Glock 18 használható első körös pisztoly, amely páncél nélküli ellenfelekkel szemben működik a legjobban, és háromlövéses sorozattűzre képes." },
	["he_grenade"] = { name = "Nagy robbanóerejű gránát", desc = "A nagy robbanóerejű repeszgránát nagy területen fejt ki rombolóhatást, ami ideálissá teszi ellenséges helyiségek megtisztítására." },
	["knife"] = { name = "Kés", desc = "A csendes és mindig kéznél lévő kés gyors, kis sebzésű első támadással és egy lassú, de magas sebzésű másodlagos támadással rendelkezik. A csendes játékosok hátbadöféssel azonnali ölést szerezhetnek." },
	["knife_t"] = { name = "Kés", desc = "A csendes és mindig kéznél lévő kés gyors, kis sebzésű első támadással és egy lassú, de magas sebzésű másodlagos támadással rendelkezik. A csendes játékosok hátbadöféssel azonnali ölést szerezhetnek." },
	["knife_gg"] = "Arany kés",
	["knife_ghost"] = { name = "Kísérteties bökő", desc = "A szellemek imádják a szellemkéseket. Vigyázz, amikor megkaparintanak egyet! Az első adandó alkalommal megpróbálnak leszúrni. Kár, hogy szellemek, és a szellemkések embernek nem árthatnak..." },
	["m249"] = { name = "M249", desc = "Az M249 erős, nyílt terepre szánt géppuska, mely tökéletes választás olyan játékosoknak, akik hajlandók elfogadni az alacsonyabb tűzgyorsaságot magasabb pontosságért és lőszerkapacitásért." },
	["m4a1"] = "M4A4",
	["m4_silencer"] = "M4A1-S",
	["m4a1_silencer"] = { name = "M4A1-S", desc = "A némítatlan társánál kisebb tárral rendelkező hangtompítós M4A1 halkabb lövéseket biztosít kisebb visszarúgással és nagyobb pontossággal." },
	["mac10"] = { name = "MAC-10", desc = "A MAC-10 géppisztoly, mely lényegében egy doboz, amiből golyók jönnek ki, magas tűzgyorsasággal büszkélkedhet, cserébe viszont rossz a szórásképe és nagy a visszarúgása." },
	["p90"] = { name = "P90", desc = "A különleges hátsótáras felépítéséről könnyen felismerhető P90 nagyszerű fegyver mozgás közbeni tüzeléshez, nagy kapacitású tára és kis visszarúgása miatt." },
	["smoke_grenade"] = { name = "Füstgránát", desc = "A füstgránát közepes kiterjedésű álcafüst-felhőt hoz létre. Hatásosan rejtheti el csapatod orvlövészek elől, vagy lehet csupán hasznos figyelem-elterelés." },
	["ump45"] = { name = "UMP-45", desc = "A géppisztolyok családjának félreértett középső gyermekének, az UMP45-nek csak a kis tára az egyetlen hátránya, egyébként sokoldalú automata közelharcfegyver." },
	["xm1014"] = { name = "XM1014", desc = "Az XM1014 erős, teljes automata sörétes puska, ami azzal szolgál rá súlyosabb árcédulájára, hogy képes gyorsan ólommal beteríteni egy szobát." },
	["c4"] = { name = "C4 robbanóanyag", desc = "Ez a stabil és a legtöbb fizikai behatásnak ellenálló rögtönzött plasztik robbanóeszköz több mint 8000 m/s detonációs sebességgel rendelkezik." },
	["bizon"] = { name = "PP-Bizon", desc = "A Bizon géppisztoly kis sebzésű, de egyedülállóan kialakított dobtárral rendelkezik, amely gyors újratöltést eredményez." },
	["mag7"] = { name = "MAG-7", desc = "A T-E exkluzív Mag-7 pusztító mennyiségű sebzést ad közelre. Gyors, szekrénytár-megoldású újratöltése nagyszerű taktikai választássá teszi." },
	["negev"] = { name = "Negev", desc = "A Negev egy szörnyeteg, ami hajszálpontos zárótüzével sakkban tudja tartani az ellenséget, feltéve, ha van elég időd átvenni az irányítást a szóródás felett." },
	["sawedoff"] = { name = "Lefűrészelt csövű sörétes", desc = "A klasszikus lefűrészelt csövű sörétes nagyon súlyos sebzést okoz közelre, de alacsony pontossága, nagy szórása és alacsony tűzgyorsasága miatt jobb, ha meg is ölöd, amit eltaláltál." },
	["tec9"] = { name = "Tec-9", desc = "A mozgásban levő Terrorista ideális pisztolya, a Tec-9 közelre halálos, és nagy tárkapacitással rendelkezik." },
	["taser"] = { name = "Zeus x27", desc = "A közeli lesből támadásokhoz és zárt helyi találkozásokhoz tökéletes egylövetű x27 Zeus egy találattal képes magatehetetlenné tenni egy ellenséget." },
	["molotov"] = { name = "Molotov-koktél", desc = "A Molotov erős és kiszámíthatatlan terület-lezáró fegyver, mely eldobva lángba borítja a földet, megsebezve minden játékost a hatósugarában." },
