	["hkp2000"] = { name = "P2000", desc = "A pontos és jól kezelhető német gyártású P2000 használható első körös pisztoly, mely páncélozatlan ellenfelek ellen működik a legjobban." },
	["mp7"] = { name = "MP7", desc = "A sokoldalú de drága német gyártású MP7 géppisztoly tökéletes választás a nagy hatású közelharcokban." },
	["mp5sd"] = { name = "MP5-SD", desc = "A gyakran, de mindig sikertelenül másolt ikonikus MP5 talán a világ legsokoldalúbb és legnépszerűbb géppisztolya. Az SD változat beépített hangtompítóval érkezik, egy már félelmetes fegyvert suttogóan csendessé téve." },
	["mp9"] = { name = "MP9", desc = "A Svájcban gyártott élvonalbeli MP9 géppisztoly egy ergonomikus polimer fegyver, amely a magán biztonsági cégek nagy kedvence." },
	["nova"] = { name = "Nova", desc = "A Nova potom árcédulája miatt nagyszerű rajtaütő fegyver egy pénzszűkében levő csapatnak." },
	["p250"] = { name = "P250", desc = "A kis visszarúgású és nagy tűzgyorsaságú P250 viszonylag olcsó választás páncélozott célok ellen." },
	["cz75"] = "CZ75-Auto",
	["scar20"] = { name = "SCAR-20", desc = "A SCAR-20 félautomata mesterlövész puska, mely nagy tűzgyorsaságot és erős távolsági sebzést kínál lassú mozgásért és magas árért cserébe." },
	["sg556"] = "SG 553",
	["ssg08"] = { name = "SSG 08", desc = "A forgó-tolózáras SSG08 kis sebzésű, de nagyon költséghatékony mesterlövész puska, így okos választás a korai körökben nagy távolságú lövészethez." },
