	["cutters"] = "Kimenekítő készlet",
	["revolver"] = { name = "R8 revolver", desc = "Az R8 revolver nagyon pontos és erős lövedéket juttat célba, hosszú ravaszút árán. A kakas kézi felhúzásával végrehajtott gyorstűz lehet a legjobb lehetőség, amikor közelre van szükség megállítóerőre." },
	["usp_silencer"] = { name = "USP-S", desc = "A Counter-Strike: Source közönség-kedvence, a hangtompítós USP pisztoly leszerelhető hangtompítóval rendelkezik, ami egyszerre csökkenti a lövés visszarúgását és nyomja el figyelemfelkeltő zaját." },
	["knifebayonet"] = { name = "Bajonett", desc = "A felépítésében a Második Világháború óta viszonylag változatlan bajonett máig megtartotta helyét a modern katonai stratégiában. A bajonettroham továbbra is hatékony, még olyan közelmúltbeli helyszíneken is, mint a Második Öböl-háború, vagy az Afganisztáni Háború." },
	["knifeflip"] = { name = "Összecsukható kés", desc = "Az összecsukható késnek perzsa stílusú, hegyes, hátrafelé ívelt pengéje van. Bár maga a hegy törékeny lehet, az összecsukható kés teljes felépítését tekintve meglepően erős." },
	["knifegut"] = { name = "Zsigerelőkés", desc = "A belezőkés legszembetűnőbb jellemzője a penge gerincén levő belezőkampó. Az eredetileg az elejtett vad helyben való megnyúzásához népszerűvé vált belezőkampó hatékony olyan szálas anyagok könnyű elvágásában is, mint a kötél, háló és biztonsági öv." },
	["knifecss"] = { name = "Klasszikus Kés", desc = "A Counter-Strike sorozat klasszikusa; a sajtolással illesztett stellit penge tökéletesen áll a helyén, precíz illesztésének köszönhetően a penge lapján és fokán levő titánnal. A markolat szénszálas foglalatba illesztett masztodoncsont." },
	["knifem9"] = { name = "M9 bajonett", desc = "Ez az M-9 bajonett. Eredetileg karabélyra rögzítésre szánták, de közelharcra is igen alkalmas." },
	["knifekaram"] = { name = "Karambit", desc = "Az ívelt pengéjével tigriskarmot utánzó karambitot a silat nevű délkelet-ázsiai harcművészeti ág részeként fejlesztették ki. A kést tipikusan fordított fogással használják, az ujjgyűrűt a mutatóujjra húzva." },
	["knifetactical"] = { name = "Vadászkés", desc = "Egy kés, melyet a modern, taktikai használatra terveztek, pengéje jól alkalmazható mind harci, mind utilitarista helyzetekben. Az egyedülálló Tanto penge lehetővé teszi a maximális áthatolást még a legkeményebb felületeken is." },
	["knife_butterfly"] = { name = "Pillangókés", desc = "Ez egy egyedi kialakítású balisong, ismertebb nevén pillangókés. E fegyver meghatározó jellemzője a legyezőszerűen kinyitható, szabadon forgó penge, ami gyors kinyitást és elrejtést tesz lehetővé. Ennek eredményeképp a pillangókés számos országban be van tiltva." },
	["partypopper"] = "Bulipuska!",
	["knife_falchion_advanced"] = { name = "Handzsárkés", desc = "Modern tisztelgés egy handzsár kard előtt; ennek a szúrópengének ívelt éle van." },
	["knife_push"] = { name = "Árnyéktőr", desc = "A hatékony brutalitásra tervezett döfőtőrt használni olyan egyszerű, mint bevinni egy-két ütést." },
	["knife_survival_bowie"] = { name = "Bowie kés", desc = "Ezt a teljes markolattüskés Bowie kést erős igénybevételre tervezték, kegyetlen túlélési helyzetekhez." },
