	["axe"] = { name = "Fejsze", desc = "A nehéz és mindig kéznél lévő fejszének van egy éles és egy tompa oldala. Lengesd meg egy alacsony sebzésű elsődleges támadáshoz vagy dobd el egy magas sebzésű másodlagos támadáshoz. Nagy fába vágtad a fejszédet." },
	["hammer"] = { name = "Kalapács", desc = "A kalapács mindig kéznél van dolgok megjavítására és összetörésére. Lengesd meg egy alacsony sebzésű elsődleges támadáshoz vagy dobd el egy magas sebzésű másodlagos támadáshoz. Ha csak kalapácsod van, egyszerre minden szögnek tűnik." },
	["spanner"] = { name = "Csavarkulcs", desc = "A csavarkulcsot jellemzően nyomaték kifejtésére használják tárgyak forgatásakor, de bármit megüthetsz vele egy alacsony sebzésű elsődleges támadáshoz vagy eldobhatod egy magas sebzésű másodlagos támadáshoz. Csavarkulccsal érkezni egy tűzharcba nem vall csavaros észjárásra." },
	["tripwirefire"] = "TrapFire",
	["knife_cord"] = { name = "Paracord kés", desc = "Ezt a fix pengéjű túlélőkést úgy tervezték, hogy kibírja a fegyverként és szerszámként való használatot is, így lehet vele csapdát készíteni, állatokra vadászni, vagy növényzetet vágni. A markolatára ejtőernyőzsinór van tekerve." },
	["knife_canis"] = { name = "Túlélőkés", desc = "Ennek a többfunkciós taktikai késnek fogazott éle van kemény anyagok, mint a csont vagy szövedékek átvágásához, plusz egy éles belezőhorga. A kompozit anyagú markolat hatlapfejű csavarokkal van a pengéhez rögzítve." },
	["knife_ursus"] = { name = "Ursus kés", desc = "Ez a tanto jellegű túlélőkés fazettás pengével és ütővégsúlyban végződő teljes markolattüskével rendelkezik. Semmi csicsa, semmi mozgó alkatrész, csak egy megbízható penge, ami munkára kész." },
	["knife_gypsy_jackknife"] = { name = "Navaja kés", desc = "Ez a márványlakk markolatú összecsukható kés egy kicsi, de gonoszul hegyes pengét rejt." },
	["knife_outdoor"] = { name = "Nomád kés", desc = "Ez az ergonomikus, becsukható pengéjű taktikai vadászkés kompozit markolatbetétekkel és széles, erős pengével rendelkezik, ami vágáshoz és tárgyak szétfeszítéséhez is hasznos." },
	["knife_widowmaker"] = { name = "Talon kés", desc = "Ez az elefántcsont markolatú karambit rézszegecsekkel és fűrészfogazással rendelkezik, így befelé menet vág, kifelé jövet pedig tép." },
	["knife_stiletto"] = { name = "Stiletto kés", desc = "Ennek a hosszú ideje viták, sőt, szabályozások tárgyát képező olasz stílusú stiletto rugós késnek olyan rugóval mozgatott pengéje van, ami szinte azonnal kiugratható egy kioldópöcök megnyomásával." },
	["knife_skeleton"] = { name = "Csontváz kés", desc = "Ennek a lecsupaszított markolattüskéjű késnek a markolatára szalagot tekertek a fogás javítására. A lyukba bele lehet dugni egy ujjat a stabilitás és biztonság növelésére." },
	["firebomb"] = { name = "Gyújtóbomba", desc = "A gyújtóbomba nagy hatású kísérleti fegyver, amely eldobva magas hőmérsékletű lánggal égő területet hoz létre, megégetve minden játékost nagy hatóterületén." },
	["diversion"] = { name = "Figyelemelterelő gránát", desc = "Eldobva a figyelemelterelő gránát további szektorokat emel ki a táblagépeken, további ellenségek illúzióját keltve." },
	["frag_grenade"] = { name = "Repeszgránát", desc = "A kísérleti repeszgránát nagyobb területen okoz rendkívüli mennyiségű robbanási sebzést, ami támadó és védekező taktikákhoz is ideálissá teszi." },
	["shield"] = { name = "Rohampajzs", desc = "Lövedékek eltérítésére vagy elnyelésére szolgáló védőeszköz, segít megvédeni a használóját különféle űrméretű lövedékektől." },
	["zone_repulsor"] = { name = "Visszaverő készülék", desc = "Körsugárzó részecskeárnyékoló készülék prototípus." },
	["cz75a"] = "A CZ-75-Auto, a CZ75 teljes automata változata az ideális rövidtávú választás a helyzet megfordítására és az ellenfeleid fegyvereinek megszerzésére. De mivel nagyon kevés lőszere van a tárban, erős lőszertakarékosságot követel.",
