	["cat_gear"] = "Спорядження",

	["ak47"] = { name = "АК-47", desc = "Потужний і надійний, автомат Калашникова — один з найпопулярніших у світі. Найсмертельніший при стрільбі короткими прицільними серіями." },
	["aug"] = { name = "AUG", desc = "Потужна і точна гвинтівка з оптичним прицілом AUG компенсує своє тривале перезаряджання низьким відхиленням куль та високою швидкістю стрільби." },
	["awp"] = { name = "AWP", desc = "Високі ризики і висока нагорода — сумно відома снайперська гвинтівка AWP легко впізнається за характерним звуком та принципом «один постріл, один труп»." },
	["deserteagle"] = { name = "Desert Eagle", desc = "Такий же дорогий як і потужний, Desert Eagle є знаковим пістолетом, який є важким для освоєння, але на диво точним на дальніх дистанціях." },
	["deagle"] = "Desert Eagle",
	["elites"] = { name = "Подвійні Beretta", desc = "Вогонь одночасно з двох пістолетів Beretta з великим магазином зменшує точність та збільшує час заряджання. З іншого боку, ви можете стріляти одразу двома." },
	["elite"] = "Подвійні Beretta",
	["famas"] = { name = "FAMAS", desc = "Дешевий варіант для небагатих гравців, FAMAS гарно заповнює нішу між більш дорогими гвинтівками та менш ефективними пістолетами-кулеметами." },
	["fiveseven"] = { name = "Five-SeveN", desc = "Високоточний та бронебійний, дорогий Five-Seven заряджається повільно, однак це компенсується щедрим магазином на 20 патронів та несильною віддачею." },
	["flashbang"] = { name = "Світлошумова граната", desc = "Несмертельна світлошумова граната тимчасово засліплює будь-кого, хто опинився в зоні ураження, що робить її ідеальною для використання у замкненому просторі. Гучний вибух такої гранати також тимчасово маскує звук від ходіння." },
	["g3sg1"] = { name = "G3SG1", desc = "G3SG1 коштує багато грошей і значно знижує швидкість руху, але компенсує це більшою скорострільністю, ніж у інших снайперських гвинтівок." },
	["galilar"] = { name = "Ґаліль", desc = "Найдешевший з-поміж доступних лише терористам автоматів, Ґаліль — це зручна зброя на середніх та великих дистанціях." },
	["glock18"] = { name = "Glock-18", desc = "Пістолет Glock 18 підійде для першого раунду. Він може стріляти серіями з трьох пострілів і буде найбільш ефективним проти суперників без бронежилета." },
	["he_grenade"] = { name = "Осколкова граната", desc = "Фугасна осколкова граната завдає сильні ушкодження у великій зоні, що робить її ідеальною для зачистки кімнат з ворогами." },
	["knife"] = { name = "Ніж", desc = "Тихий і завжди доступний бойовий ніж має головну швидку атаку з невеликою кількістю ушкоджень і додаткову повільну з сильними пошкодженнями. Атака зі спини дозволить тихим гравцям миттєво вбити ворога." },
	["knife_t"] = { name = "Ніж", desc = "Тихий і завжди доступний бойовий ніж має головну швидку атаку з невеликою кількістю ушкоджень і додаткову повільну з сильними пошкодженнями. Атака зі спини дозволить тихим гравцям миттєво вбити ворога." },
	["knife_gg"] = "Золотий ніж",
	["knife_ghost"] = { name = "Спектральне лезо", desc = "Привиди люблять примарні ножі. Коли вони їх роздобудуть, стережіться! Вони спробують заколоти вас за першої нагоди. Проте вони лише привиди і їхні ножі не завдають шкоди людям…" },
	["m249"] = { name = "M249", desc = "Міцний кулемет M249 призначений для відкритого простору й ідеально підходить гравцям, які готові обміняти повільний темп вогню на підвищену влучність та місткий магазин." },
	["m4a1"] = "M4A4",
	["m4_silencer"] = "M4A1-S",
	["m4a1_silencer"] = { name = "M4A1-S", desc = "З меншим магазином, аніж його аналог без глушника, безшумний карабін M4A1 забезпечує тиху стрільбу з меншою віддачею та вищою точністю." },
	["mac10"] = { name = "MAC-10", desc = "Пістолет-кулемет MAC-10 є, по суті, коробкою, з якої вилітають кулі. Він дуже швидкострільний, однак платою за це є низька точність та сильна віддача." },
	["p90"] = { name = "P90", desc = "Легко впізнаваний за унікальним дизайном булпапа, P90 є чудовою зброєю для стрільби в русі завдяки високій місткості магазина та малій віддачі." },
	["smoke_grenade"] = { name = "Димова граната", desc = "Димова граната створює димову завісу середнього радіусу. Її можна використати, щоб сховати вашу команду від снайперів, чи просто як відволікаючий маневр." },
	["ump45"] = { name = "UMP-45", desc = "Гидке каченя у родині пістолетів-кулеметів, UMP45 має єдиний недолік – невеликий магазин. Загалом же це чудова універсальна автоматична зброя для ближнього бою." },
	["xm1014"] = { name = "XM1014", desc = "XM1014 — потужний і повністю автоматичний дробовик, що виправдовує свою здоровенну ціну можливістю швидко наповнити свинцем будь-яке приміщення." },
	["c4"] = { name = "Вибухівка C4", desc = "Стабільний та стійкий до більшості механічних ударів, цей саморобний вибуховий пристрій після запалювання має швидкість детонації понад 8000 метрів за секунду." },
	["bizon"] = { name = "ПП-19 «Бізон»", desc = "Пістолет-кулемет ПП-19 («Бізон») завдає малих пошкоджень, але його особливістю є унікальний місткий барабанний магазин, що швидко перезаряджається." },
	["mag7"] = { name = "MAG-7", desc = "Ексклюзивний для ССО Mag-7 завдає приголомшливих ушкоджень з близької відстані. Швидке перезаряджання магазина робить його чудовим тактичним вибором." },
	["negev"] = { name = "Неґев", desc = "Неґев — це звір, що може утримувати ворога в укритті завдяки своєму точковому стримувальному вогню, якщо ви маєте достатньо часу, аби взяти його спершу неточну стрільбу під контроль." },
	["sawedoff"] = { name = "Обріз", desc = "Класичний обріз завдає вкрай тяжких ушкоджень з близької відстані, однак стрільба з нього неточна і характеризується високим розсіюванням та низькою швидкострільністю. Тож вам краще швидше вбити те, у що ви поцілили." },
	["tec9"] = { name = "Tec-9", desc = "Tec-9 ідеально пасує терористам, які активно пересуваються. Він смертоносний у ближньому бою і має місткий магазин." },
	["taser"] = { name = "Zeus x27", desc = "Однозарядний Zeus x27 ідеальний для засідок з близької відстані та сутичок у закритому просторі. Він здатен нейтралізувати ворога одним розрядом." },
	["molotov"] = { name = "Коктейль Молотова", desc = "Коктейль Молотова — це потужна і непередбачувана зброя, яка після падіння розливається полум’ям по невеликій території, вражаючи всіх гравців у своєму радіусі." },
	["incgrenade"] = "Запалювальна граната",
	["decoy"] = "Граната-приманка",
	["hkp2000"] = { name = "P2000", desc = "Точний і передбачуваний, виготовлений у Німеччині пістолет P2000 надається в першому раунді. Найкраще застосовувати проти ворогів без броні." },
	["mp7"] = { name = "MP7", desc = "Багатофункціональний, однак дорогий, німецький пістолет-кулемет MP7 є найкращим вибором для активних сутичок на короткій дистанції." },
	["mp5sd"] = { name = "MP5-SD", desc = "Його часто імітували, але так і не змогли повторити. Знаковий MP5, імовірно, найбільш універсальний та популярний пістолет-кулемет у світі. Цей SD-варіант має інтегрований глушник, що робить цю грізну зброю тихою, мов шепіт." },
	["mp9"] = { name = "MP9", desc = "Вироблений у Швейцарії передовий пістолет-кулемет MP9 є ергономічною полімерною зброєю, яку полюбляють приватні охоронні фірми." },
	["nova"] = { name = "Nova", desc = "Бюджетність дробовика Nova робить його поширеним вибором небагатої команди для нападу із засідки." },
	["p250"] = { name = "P250", desc = "Швидкострільний пістолет P250 має низьку віддачу і є відносно недорогим вибором проти захищених бронежилетами опонентів." },
	["cz75"] = "CZ75-авто",
	["scar20"] = { name = "SCAR-20", desc = "SCAR-20 — напівавтоматична снайперська гвинтівка, що має високу швидкострільність та завдає значних ушкоджень на великих дистанціях. Однак вона дорога і суттєво сповільнює власника." },
	["sg556"] = "SG 553",
	["ssg08"] = { name = "SSG 08", desc = "SSG08 — це снайперська гвинтівка з рухомим затвором, котра не завдає великих ушкоджень, однак має прийнятну ціну. Вона є гарним вибором у перших раундах для влучного вогню з далекої дистанції." },
	["heavyassaultsuit"] = "Штурмова броня",
	["assaultsuit"] = "Бронежилет + Шолом",
	["kevlar"] = "Бронежилет",
	["defuser"] = "Кусачки",
	["cutters"] = "Рятувальний набір",
	["revolver"] = { name = "Револьвер R8", desc = "Револьвер «R8» стріляє дуже точно та сильно, але сумнозвісний він своїм повільним спусковим гачком. Якщо ворог близько — бийте по гачку якомога швидше." },
	["usp_silencer"] = { name = "USP-S", desc = "Улюбленець гравців у «Counter-Strike: Source», беззвучний пістолет USP має від’єднувальний глушник, який дозволяє здійснювати постріли із малою віддачею та без зайвого шуму." },
	["knifebayonet"] = { name = "Штик-ніж", desc = "Його дизайн суттєво не змінювався з часів Другої світової війни, але штик-ніж усе ще зберігає місце у сучасній військовій стратегії. Він лишався ефективним у недавніх війнах у Перській затоці та Афганістані." },
	["knifeflip"] = { name = "Складний ніж", desc = "Спортивні складні ножі перського стилю зі стрілоподібним лезом та дуже гострим заточенням. Хоч саме лезо може бути слабким, але загальний дизайн складного ножа на диво міцний." },
	["knifegut"] = { name = "Ніж з лезом-гаком", desc = "Найбільш примітною особливістю ножа різника є гак на зворотній стороні леза. Спочатку застосовувався як допоміжний засіб білування тварин, але також ефективний для прорізування волокнистих матеріалів, наприклад, мотузок, тканих стрічок чи ременів безпеки." },
	["knifecss"] = { name = "Класичний ніж", desc = "Classic Knife. Класика серії ігор Counter-Strike. Край цього ножа — пресований стеліт, який чудово тримається завдяки гармонійному поєднанню з титаном на боці та вигині леза. Рукоятка створена зі скам’янілої кістки мамонта і тримається в кишені з вуглецевого волокна." },
	["knifem9"] = { name = "Штик-ніж M9", desc = "Це американський штик-ніж M-9. Спершу призначався для встановлення на гвинтівці, але добре підходить і для ближнього бою." },
	["knifekaram"] = { name = "Керамбіт", desc = "Керамбіт — ніж з вигнутим клинком, що нагадує пазур тигра. Він був розроблений, як частина бойового мистецтва сілат, що розповсюджене в південно-східній Азії. Зазвичай, ніж тримають зворотньою хваткою, просовуючи вказівний палець в спеціальне кільце на рукоятці." },
	["knifetactical"] = { name = "Мисливський ніж", desc = "Huntsman Knife\nНіж, створений для сучасних тактичних вимог, лезо якого гарно підходить як для бойових, так і для практичних потреб. Унікальний наконечник Tanto дає можливість здійснювати максимальне проникнення навіть крізь найміцніші поверхні." },
	["knife_butterfly"] = { name = "Балісонг", desc = "Butterfly Knife\nЦе спеціально розроблений балісонг, також відомий як ніж-метелик. Визначальною характеристикою цієї зброї є легке витягнення вільного у обертанні леза віялоподібним рухом, що дозволяє швидко оголити та приховати лезо. Як результат, ножі-метелики заборонені законами багатьох країн." },
	["partypopper"] = "Хлопавка!",
	["knife_falchion_advanced"] = { name = "Ніж-фальшіон", desc = "Falchion Knife\nЦей ніж з оберненою курватурою обуха, сучасне переосмислення меча-фальшіона, має характерне загнуте вістря." },
	["knife_push"] = { name = "Тіньові кинджали", desc = "Створенні ефективними та брутальними, адже наносити удари такими ножами просто як  кулаками." },
	["knife_survival_bowie"] = { name = "Ніж Боуї", desc = "Цей цілісний зазубрений ніж Боуї створений для виживання у важких екстремальних умовах." },
	["healthshot"] = "Медичний шприц",
	["tagrenade"] = "Тактична відстежувальна граната",
	["exploding_barrel"] = "Вибухова бочка",
	["snowball"] = "Сніжка",
	["stompdamagekillstring"] = "розбиває вам череп",
	["dangerzone"] = "Небезпечна зона",
	["autosentry"] = "Автоматична турель",
	["fists"] = "Кулаки",
	["tablet"] = "Планшет",
	["breachcharge"] = "Пробивний заряд",
	["bumpmine"] = "Міна для стрибків",
	["axe"] = { name = "Сокира", desc = "Сильна й завжди доступна сокира має як гостру, так і тупу сторону. Ви можете змахнути нею й завдати малої шкоди, або ж кинути й завдати великої шкоди. Якщо не знайдете деревини для рубання, вороги підійдуть також." },
	["hammer"] = { name = "Молоток", desc = "Молотком можна як лагодити, так і руйнувати речі. Ви можете бити ним і завдати малої шкоди, або ж кинути й завдати великої шкоди. Якщо ви маєте лише молоток, то все скрізь здаватиметься цвяхами." },
	["spanner"] = { name = "Гайковий ключ", desc = "Гайковий ключ зазвичай використовується для закручування чи відкручування предметів. Але ви можете бити ним і завдати малої шкоди, або ж кинути й завдати великої шкоди. Хочете зганьбитися — принесіть гайковий ключ на перестрілку." },
	["tripwirefire"] = "TrapFire",
	["knife_cord"] = { name = "Ніж десантника", desc = "Цей ніж для виживання із зафіксованим лезом створений, щоби слугувати як зброєю, так і інструментом, наприклад, для встановлення пасток, полювання на тварин чи обтинання листя. Руків’я обгорнене довгим універсальним паракордом." },
	["knife_canis"] = { name = "Ніж уцілілого", desc = "Цей універсальний тактичний ніж має зубчатий край для розрізання грубих матеріалів, як-от кіста чи волокно, а також гострий гак для патрання. Композитний матеріал руків’я прикріплений до леза шестигранними гайками." },
	["knife_ursus"] = { name = "Ведмежий ніж", desc = "Ursus Knife. Тантоподібний ніж для виживання має грановане лезо, широкий хвостовик, а також рукоятку для завдання ударів. Немає зайвих дрібниць, немає рухомих частин – просто надійне лезо, готове до роботи." },
	["knife_gypsy_jackknife"] = { name = "Наваха", desc = "Navaja Knife. Цей складний ніж з емальованою ручкою приховує маленьке, але смертельно загострене лезо." },
	["knife_outdoor"] = { name = "Ніж волоцюги", desc = "Цей ергономічний тактичний мисливський ніж із зафіксованим лезом має композитне руків’я, що вставлене в широке й міцне лезо, зручне для розрізання та проштрикування." },
	["knife_widowmaker"] = { name = "Кіготь", desc = "Talon Knife. Цей керамбіт зі слонової кістки має латунні заклепки та гострі, мов кігті, гребні, які ріжуть при входженні та розривають при виведенні леза." },
	["knife_stiletto"] = { name = "Ніж-стилет", desc = "Stiletto Knife. Історично дуже популярний італійський ніж-стилет, який можна витягти практично миттєво завдяки пружинному механізму. Просто натисніть кнопку фіксатора." },
